function shuffleArray(e) {
    for (let t = e.length - 1; t > 0; t--) {
        const i = floor(R.random_dec() * (t + 1));
        [e[t], e[i]] = [e[i], e[t]]
    }
}

function packCircle() {
	
    let e;
    let t = 0;
    for (; !e && t < confidence;) {
        const i = orientation==="Portrait" ? (R.random_dec() * (width / 2 - width / pj)) + (width / pj): (R.random_dec() * (height / 2 - height / pj)) + (height / pj),
            r = R.random_num(marg, width-marg),
            n = R.random_num(marg, height-marg);
        e = new Circle(r, n, i);
        for (let t = 0; t < circles.length; t++)
            if (e.intersects(circles[t])) {
                e = null;
                break;
            } t++
    }
    e && circles.push(e)
}

class Circle {
    constructor(e, t, i) {
        this.x = e, this.y = t, this.r = i
    }
    intersects(e) {
        return dist(this.x, this.y, e.x, e.y) < this.r + e.r
    }
    draw() {
        noStroke();
		let concentrics = R.random_int(1,9);
        for (let e = concentrics, t = (rGirth * fSc) / concentrics; e > 0; e--) {
			if (R.random_dec()<0.5){
				fill(R.random_choice(currentPalette));
			} else {
				stroke(R.random_choice(currentPalette));
			}
			ellipse(this.x, this.y, t * e);
		}
    }
	draw3() {
        noFill();
		let concentrics = R.random_int(1,9);
        for (let e = concentrics, t = (rGirth * fSc) / concentrics; e > 0; e--) {
			if (R.random_dec()<0.5){
				stroke(R.random_choice(acurrentPalette));
			} else {
				stroke(R.random_choice(acurrentPalette));
			}
			ellipse(this.x, this.y, this.r);
		}
    }
	draw2() {
        noStroke();
		let concentrics = R.random_int(1,9);
        for (let e = concentrics, t = (rGirth * fSc) / concentrics; e > 0; e--) {
			if (R.random_dec()<1){
				fill("#00000022");
			} else {
				stroke(R.random_choice(currentPalette));
			}
			ellipse(this.x, this.y + 2, (t* e)+2);
		}
    }
}

function surfacing(e = 20) {
    loadPixels();
    const t = pixelDensity(),
        i = -e,
        r = e,
        n = width * t * 4 * (height * t);
    for (let e = 0, t; e < n; e += 4) {
		t = R.random_int(i, r);
				
		let ttt = [e, e+1, e+2];
        pixels[e] = pixels[ttt[0]] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[ttt[1]] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[ttt[2]] + t
    }
    updatePixels()
}

function surfacing2(e = 5) {
    loadPixels();
    let t = pixelDensity(),
        i = -e,
        r = e,
        n = width * t * 4 * (height * t);
		[f1,f2,f3,f4]=[R.random_dec(),R.random_dec(),R.random_dec(),R.random_dec()];
    for (let e = 0; e < n; e += 4) {
		if (pixels[e]===bckColorRGB[0] && pixels[e+1]===bckColorRGB[1] && pixels[e+2]===bckColorRGB[2]){
			pixels[e] = pixels[e] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e + 1] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e + 2] + t
    
			continue;
		}
		
		if (e<f1*n){
			let t = R.random_int(i, r);
			pixels[e] = pixels[e + 1] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e + 1] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e + 2] + t
		} else if (e<f2*n) {
			let t = R.random_int(i, r);
			pixels[e] = pixels[e+2] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e + 1] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e + 2] + t
		} else if (e<f3*n) {
			let t = R.random_int(i, r);
			pixels[e] = pixels[e+2] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e + 2] + t
		} else if (e<f4*n) {
			let t = R.random_int(i, r);
			pixels[e] = pixels[e+2] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e] + t
		} else {
			let t = R.random_int(i, r);
			pixels[e] = pixels[e] + t, t = R.random_int(i, r), pixels[e + 1] = pixels[e + 1] + t, t = R.random_int(i, r), pixels[e + 2] = pixels[e + 2] + t
		}
	}
    updatePixels()
}

