class chunk {
	constructor(x, y, r, c = 0, pt = [1200, 700]) {
this.x = x * (width / canvasSize[0]);
this.y = y * (height / canvasSize[1]);

		this.r = r;
		this.c = c;
		this.pt = pt;
this.pt[0] = windowWidth / 2 + random([1, -1]) * 5000;
	}
	
	draw() {
		    strokeCap(ROUND);
    stroke(0);
    strokeWeight(3);
    fill(this.c);
    
    let dt = d(this.x - this.pt[0], this.y - this.pt[1]);
    let tx = this.x + this.r * (this.pt[0] - this.x) / dt;
    let ty = this.y + this.r * (this.pt[1] - this.y) / dt;
    
    push();
    translate(10 * (this.pt[0] - this.x) / dt, 10 * (this.pt[1] - this.y) / dt - 3);
    beginShape();
    
    vertex(this.x, this.y);
    vertex(tx, ty);
    vertex(tx, ty + h);
    vertex(this.x,  this.y + h);
    endShape(CLOSE);
    pop();
    
    beginShape();
    vertex(this.x, this.y);
    vertex(tx, ty);
    vertex(tx, ty + h);
    vertex(this.x,  this.y + h);
    endShape(CLOSE);
	}
	
	check1(){
		let dt = d(this.x - this.pt[0], this.y - this.pt[1]);
		let tx = this.x + this.r * (this.pt[0] - this.x) / dt;
		let ty = this.y + this.r * (this.pt[1] - this.y) / dt;
		return tx < h - 100 && tx > 100;
	}
}

function bigpal(div, p) {
	let out = [];
	for (let i = 1; i < p.length; i++) {
		out.push(color(p[i - 1]));
		for (let j = 1; j < div; j++) {
			out.push(lerpColor(color(p[i - 1]), color(p[i]), j * (1 / div)));
		}
	}
	out.push(color(p[p.length - 1]));
	return out;
}

