function draw() {
if (stopDrawing || lineCount >= maxLines) return;
offscreen.push();
  for (let j = 0; j < iters; ++j) { 
    if (t % 20 == 0) {
      let start_r = 380;
      nx = R.random_num(-start_r, start_r) + width / 2;
      ny = R.random_num(-start_r, start_r) + height / 2;
      x = nx;
      y = ny;
      let cc = bp[int(noise(x / scn_color, y / scn_color) * bp.length) % bp.length];
    offscreen.stroke(cc);  // changed
    }
    
    t += 1;
    nx += (2.0 * noise(x * scn, y * scn) - 1) * mag;
    ny += (2.0 * noise(x * scn - 1, y * scn + 1) - 1) * mag;
    
    oldR = r;
    mX = nx;
    mY = ny;
    ax += (mX - x) * spring;
    ay += (mY - y) * spring;
    ax *= friction;
    ay *= friction;
    a += sqrt(ax * ax + ay * ay) - a;
    a *= 0.6;
    r = size - a;

    for (let i = 0; i < distance; ++i) {
      oldX = x;
      oldY = y;
      x += ax / distance;
      y += ay / distance;
      oldR += (r - oldR) / distance;
      if (oldR < 1) oldR = 1;
    offscreen.strokeWeight(oldR + diff);  // changed
    offscreen.line(x, y, oldX, oldY);  // changed
      strokeWeight(oldR);
      line(x + diff * 2, y + diff * 2, oldX + diff * 2, oldY + diff * 2);
      line(x - diff, y - diff, oldX - diff, oldY - diff);
    }
      offscreen.pop();
  image(offscreen, 0, 0, width, height);
    lineCount++;
  }
}

