function Particle(x, y) {
  this.x = x;
  this.y = y;
  this.pos = createVector(this.x, this.y);

  this.life = R.random_dec();
  this.c = color(R.random_choice(colors));
  this.ff = 0;

  this.update = function () {
    this.ff = noise(this.pos.x / 100, this.pos.y / 100) * TWO_PI;
    let mainP = 1200;
    let changeDir = TWO_PI / mainP;
    let roundff = round((this.ff / TWO_PI) * mainP);
    this.ff = changeDir * roundff;

    if (this.ff < 6 && this.ff > 3) {
      this.c = colors[0];
      stroke(this.c);
      this.pos.add(tan(this.ff) * R.random_num(1, 3), tan(this.ff));
    } else {
      this.c = colors[1];
      stroke(this.c);
      this.pos.sub(sin(this.ff) * R.random_num(0.1, 1), cos(this.ff));
    }
  };

  this.show = function () {
    noFill();
    strokeWeight(R.random_dec()*1.25);
    let lx = 20;
    let ly = 20;
    let px = constrain(this.pos.x, lx, width - lx);
    let py = constrain(this.pos.y, ly, height - ly);
    point(px, py);
  };

  this.finished = function () {
    this.life -= R.random_dec() / 10;
    this.life = constrain(this.life, 0, 1);
    if (this.life == 0) {
      return true;
    } else {
      return false;
    }
  };
}

