class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}


let shapeFamilies = [];
const PALETTES = [
  ['#f72585', '#7209b7', '#4361ee', '#4cc9f0', '#f9e900'],
  ['#0a2463', '#fb3640', '#605f5e', '#247ba0', '#e2e2e2'],
  ['#fffffa', '#515052', '#000103', '#333138', '#ff312e', '#cc5a71', '#e54b4b'],
  ['#ee1d38', '#2d55e6', '#FFE149', '#000000', '#ffffff'],
  ['#011627', '#ff3366', '#2ec4b6', '#f6f7f8', '#20a4f3'],
  ['#f73939', '#ffda33', '#0c4896', '#48b7f7', '#f7f7f7', '#2b2b2b'],
  ['#000f08', '#136f63', '#e0ca3c', '#f34213', '#3e2f5b']];

let R;
let seed;

function setup() {
  R = new Random();
  seed = R.random_int(0, 99999);
  randomSeed(seed);
  noiseSeed(seed);

  createCanvas(windowWidth, windowHeight);

  R_SIZE = Math.min(windowWidth, windowHeight) * 0.08;
  GAP_SIZE = R_SIZE * 0.75;
  SCALE_FACTOR = windowWidth / 800;

  backgroundPalette = PALETTES[R.random_int(0, PALETTES.length - 1)];
  background(R.random_choice(backgroundPalette));

  const numRows = R.random_choice([2, 3, 5]);
  const gridSize = numRows * R_SIZE + (numRows - 1) * GAP_SIZE;
  const startX = (width - gridSize) / 2;
  const startY = (height - gridSize) / 2;

  const paletteIndex = R.random_int(0, PALETTES.length - 1);
  const palette = PALETTES[paletteIndex];
const colors = deterministicShuffle([...palette], R);

  for (let row = 0; row < numRows; row++) {
    for (let col = 0; col < numRows; col++) {
      const x = startX + R_SIZE * row + GAP_SIZE * row;
      const y = startY + R_SIZE * col + GAP_SIZE * col;
      if (x + R_SIZE <= width && y + R_SIZE <= height) {
        shapeFamilies.push(new ShapeFamily(x, y, colors));
      }
    }
  }
}

function draw() {
  shapeFamilies.forEach((shapeFamily) => {
    shapeFamily.draw();
  });
}

class ShapeFamily {
  constructor(x, y, colors) {
    const _shapes = [
      { w: R.random_num(40, 50) * SCALE_FACTOR, h: R.random_num(75, 95) * SCALE_FACTOR },
      { w: 40 * SCALE_FACTOR, h: 40 * SCALE_FACTOR },
      { w: R.random_num(8, 16) * SCALE_FACTOR, h: R.random_num(40, 55) * SCALE_FACTOR },
      { w: R.random_num(15, 25) * SCALE_FACTOR, h: R.random_num(10, 15) * SCALE_FACTOR }
    ];
    const smallShapeAmount = R.random_int(3, 8);
    for (let i = 0; i < smallShapeAmount; i++) {
      const size = R.random_num(4, 10) * SCALE_FACTOR;
      _shapes.push({ w: size, h: size });
    }
    this.shapes = _shapes.map(({ w, h }, index) => new Rec({ w, h, clr: colors[index % colors.length] }));
    this.x = x;
    this.y = y;
  }

  draw() {
    push();
    translate(this.x, this.y);
    this.shapes.forEach((rec) => {
      rec.draw(0, 0);
    });
    pop();
  }
}

class Rec {
  constructor({ w, h, clr }) {
    this.w = w;
    this.h = h;
    this.x = 0;
    this.y = 0;
    this.setPosition();
    this.clr = clr;
  }

  setPosition() {
    const [w, h] = this.getSize();
    this.x = R.random_num(w / 2, this.w - w / 2);
    this.y = R.random_num(h / 2, this.h - h / 2);
    this.w = w;
    this.h = h;
  }

  getSize() {
    return R.random_choice([
      [this.w, this.h],
      [this.h, this.w]
    ]);
  }

  draw(roomX, roomY) {
    push();
    translate(roomX, roomY);
    fill(this.clr);
    noStroke();
    rectMode(CENTER);
    rect(this.x, this.y, this.w, this.h);
    pop();
  }
}

function deterministicShuffle(array, R) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = R.random_int(0, i);
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

let particles = [];
let wormholePosX, wormholePosY;
let vortexStrength = 0.1;
let wormholeSize = 25;

// Funzione per generare un colore casuale hexadecimal valido
function randomHexColor() {
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

// Scegli casualmente un colore dall'array di 500 colori
let randomColor = randomHexColor();

function setup() {
  createCanvas(windowWidth, windowHeight);
  wormholePosX = width / 2;
  wormholePosY = height / 2;

  for (let i = 0; i < 10000; i++) {
    let x = random(width);
    let y = random(height);
    let speed = random(0.001, 0.005);
    let angle = random(TWO_PI);
    let particle = new Particle(x, y, speed, angle, randomColor); // Usa lo stesso colore per tutte le particelle
    particles.push(particle);
  }
}

function draw() {
  background(255);

  for (let particle of particles) {
    let force = createVector(wormholePosX - particle.position.x, wormholePosY - particle.position.y);
    force.normalize();
    force.mult(vortexStrength);
    particle.applyForce(force);

    particle.update();
    particle.checkCollision();
    particle.display();
  }

  fill(0);
  noStroke();
  ellipse(wormholePosX, wormholePosY, wormholeSize * 2);
}

class Particle {
  constructor(x, y, speed, angle, color) {
    this.position = createVector(x, y);
    this.velocity = p5.Vector.fromAngle(angle).mult(speed);
    this.radius = 2;
    this.color = color; // Ogni particella avrà lo stesso colore
  }

  applyForce(force) {
    this.velocity.add(force);
  }

  update() {
    this.position.add(this.velocity);
  }

  checkCollision() {
    if (
      this.position.x < 0 ||
      this.position.x > width ||
      this.position.y < 0 ||
      this.position.y > height
    ) {
      this.velocity.mult(-1);
    }

    let d = dist(this.position.x, this.position.y, wormholePosX, wormholePosY);
    if (d < wormholeSize) {
      this.position.x = random(width);
      this.position.y = random(height);
      this.velocity.mult(0);
    }
  }

  display() {
    fill(this.color); // Usa lo stesso colore assegnato
    noStroke();
    ellipse(this.position.x, this.position.y, this.radius * 2);
  }
}

function windowResized() {
  resizeCanvas(windowWidth, windowHeight);
  wormholePosX = width / 2;
  wormholePosY = height / 2;
}

