// Wait for the DOM to be fully loaded before appending canvas elements
document.addEventListener("DOMContentLoaded", function() {
  // Initialize Paper.js
  paper.install(window);

  // 🪟 Initialize viewport dimensions
  let viewportWidth = window.innerWidth;
  let viewportHeight = window.innerHeight;

  // 🖼️🎨 Create background and CTF canvases
  let backgroundCanvas = document.createElement('canvas');
  let CTFCanvas = document.createElement('canvas');
  document.body.appendChild(backgroundCanvas);
  document.body.appendChild(CTFCanvas);
  const backgroundContext = backgroundCanvas.getContext('2d');

  // Initialize frame count and hue shift for effects and time
  let frameCount = 0;
  let hueShift = 0;

  // 🎨 Initialize CTF variables
  let externalRadiusFactor = 2;
  let internalRadiusFactor = 1;
  let initialRadius = Math.min(viewportWidth, viewportHeight) * 0.08;
  let dynamicRadius = initialRadius;

  // 🎨🔨 New global variables for generative attributes
  let changeSidesFrame = 0;  // Frame at which the number of sides changes
  let maxNumberOfSides = 10; // Maximum number of sides for the polygons
  let numberOfSides = 3;  // Number of sides for the polygons
  let scale = 1;          // Scale factor for the shapes
  let rotation = 0;       // Rotation angle for the shapes
  let innerRoundness = 0; // Roundness for the inner polygon
  let outerRoundness = 0; // Roundness for the outer polygon
  let bezierCurves = [];  // Bezier curve control points for custom shapes
  let vertexAngle = 0;  // Initialize to 0
  let segmentOffsetFactor = 0;  // Initialize to 0
  let growingSidesFactor = 0; // Initialize to 0

 // 🪟 Function to resize the canvas
  function resizeCanvas() {
    document.body.style.padding = '0';
    document.body.style.overflow = 'hidden';  // Add this line to hide overflow
    
    viewportWidth = window.innerWidth;
    viewportHeight = window.innerHeight;
    paper.view.viewSize = new paper.Size(viewportWidth, viewportHeight);
    backgroundCanvas.width = viewportWidth;
    backgroundCanvas.height = viewportHeight;
    backgroundCanvas.style.width = viewportWidth + "px";
    backgroundCanvas.style.height = viewportHeight + "px";
    CTFCanvas.width = viewportWidth;
    CTFCanvas.height = viewportHeight;
    CTFCanvas.style.width = viewportWidth + "px";
    CTFCanvas.style.height = viewportHeight + "px";
    
    // Recalculate initial and dynamic radius based on new dimensions
    initialRadius = Math.min(viewportWidth, viewportHeight) * 0.08;
    dynamicRadius = initialRadius;
  }

  // 🖼️ Function to draw a solid black background
  function drawBackground() {
    backgroundContext.fillStyle = "#000000"; // Set the fill color to black
    backgroundContext.fillRect(0, 0, viewportWidth, viewportHeight); // Fill the entire canvas
  }

  // 🎨 Function to create the base shape (Star-like polygon), CTF
  function createGroup(center, radius, rotation = 0, sides = 3, scale = 1) {
    // Create the "next" polygon with one additional side
    let nextSides = sides + 1;
    
    let currentOuterPolygon = new paper.Path.RegularPolygon(center, sides, radius * externalRadiusFactor * scale);
    let nextOuterPolygon = new paper.Path.RegularPolygon(center, nextSides, radius * externalRadiusFactor * scale);
    
    let currentInnerPolygon = new paper.Path.RegularPolygon(center, sides, radius * internalRadiusFactor * scale);
    let nextInnerPolygon = new paper.Path.RegularPolygon(center, nextSides, radius * internalRadiusFactor * scale);

    // Modified Interpolate function
    function interpolateVertices(currentPolygon, nextPolygon, pivotIndex) {
      for (let i = 0; i < currentPolygon.segments.length; i++) {
        let curPoint = currentPolygon.segments[i].point;
        let nextIndex = Math.floor(i * nextPolygon.segments.length / currentPolygon.segments.length);

        // Selectively update segments based on the pivot index
        if (i === pivotIndex) {
          nextIndex = nextPolygon.segments.length - 1; // Use the last point as the new side
        }

        let nextPoint = nextPolygon.segments[nextIndex].point;
        currentPolygon.segments[i].point = curPoint.add(nextPoint.subtract(curPoint).multiply(growingSidesFactor));
      }
    }

    // Update: Pass pivot index (0 means the top point will be the pivot)
    interpolateVertices(currentOuterPolygon, nextOuterPolygon, 0);
    interpolateVertices(currentInnerPolygon, nextInnerPolygon, 0);

    currentOuterPolygon.smooth({ type: 'catmull-rom', factor: outerRoundness });
    currentInnerPolygon.smooth({ type: 'catmull-rom', factor: innerRoundness });

    currentOuterPolygon.rotate(rotation, center);
    currentInnerPolygon.rotate(rotation, center);

    // 🎨🔨 Adjust vertex angles dynamically
    for (let i = 0; i < currentOuterPolygon.segments.length; i++) {
      let point = currentOuterPolygon.segments[i].point;
      let vector = point.subtract(center);
      vector.angle += vertexAngle * growingSidesFactor; // Use growingSidesFactor here
      currentOuterPolygon.segments[i].point = center.add(vector);
    }

    for (let i = 0; i < currentInnerPolygon.segments.length; i++) {
      let point = currentInnerPolygon.segments[i].point;
      let vector = point.subtract(center);
      vector.angle += vertexAngle * growingSidesFactor;
      currentInnerPolygon.segments[i].point = center.add(vector);
    }

    // Manipulate the segment point positions to distort your shape
    for (let i = 0; i < currentOuterPolygon.segments.length; i++) {
      let segment = currentOuterPolygon.segments[i];
      segment.point.x += Math.sin(i + frameCount * 0.01) * segmentOffsetFactor;
      segment.point.y += Math.cos(i + frameCount * 0.01) * segmentOffsetFactor;
    }

