      let segment = currentInnerPolygon.segments[i];
      segment.point.x += Math.sin(i + frameCount * 0.01) * segmentOffsetFactor;
      segment.point.y += Math.cos(i + frameCount * 0.01) * segmentOffsetFactor;
    }

    let polystar = currentOuterPolygon.exclude(currentInnerPolygon);
    currentOuterPolygon.remove();
