    // Manually update the hue for each color stop in the gradient
    for (let i = 0; i < gradient.stops.length; i++) {
      let color = gradient.stops[i].color;
      color.hue += hueShift;
    }

    // Create a gradient color object that starts and ends at the bounds of the currentShape
    let gradientColor = new paper.Color(gradient, currentShape.bounds.topLeft, currentShape.bounds.bottomRight);

    // Set fill color to black
    if (currentShape && !currentShape.isEmpty()) {
      currentShape.fillColor = new paper.Color(255, 255, 255);  // black color
      currentShape.blendMode = 'difference';
    }

    /*// Apply the gradient as the fill color
    if (currentShape && !currentShape.isEmpty()) {
      currentShape.fillColor = gradientColor;
      currentShape.blendMode = 'difference';
    }*/

    // Clone the original central shape for tessellation
    let originalShapeForTessellation = currentShape.clone();

    // Remove all children from the active layer so it doesn't overlap the tessellation grid
    paper.project.activeLayer.removeChildren();
    
    // Now use originalShapeForTessellation to generate the tessellated grid in tessellateCTF function
    if (originalShapeForTessellation && !originalShapeForTessellation.isEmpty()) {
      tessellateCTF(originalShapeForTessellation);
    } 
  }

  // 🎨 function: tessellateCTF alternating mirror tessellation grid
  function tessellateCTF(originalShapeForTessellation) {
    if (!originalShapeForTessellation) {
      return; // Exit function if originalShapeForTessellation is null or undefined
    }

    // The last shape created in drawTessellationShape will be assumed to be the base for tessellation
    let baseShape = originalShapeForTessellation;
    
    // Calculate the viewport dimensions
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    // Calculate the size of a single tessellation unit
    let unitWidth = 2 * dynamicRadius;
    let unitHeight = 2 * dynamicRadius;

    // The number of rows and columns needed to fill the viewport
    let cols = Math.ceil(viewportWidth / unitWidth) + 1;
    let rows = Math.ceil(viewportHeight / unitHeight) + 1;

    // If the window is too small, adjust cols and rows to fit
    if (viewportWidth < unitWidth * 2) {
      cols = 1;
    }
    if (viewportHeight < unitHeight * 2) {
      rows = 1;
    }

    // Function to handle flipping
    function handleFlip(clone, flipHorizontal, flipVertical) {
      if (flipHorizontal && flipVertical) {
        clone.scale([-1, -1]); // Flip both horizontally and vertically
      } else if (flipHorizontal) {
        clone.scale([-1, 1]); // Flip horizontally
      } else if (flipVertical) {
        clone.scale([1, -1]); // Flip vertically
      }
    }

    // Create columns
    for (let i = -cols; i <= cols; i++) {
      let flipCol = (i + cols) % 2 === 1;
      
      // Create rows for each column
      for (let j = -rows; j <= rows; j++) {
        let flipRow = (j + rows) % 2 === 1;
        let clone = originalShapeForTessellation.clone();
        
        let x_offset = i * unitWidth;
        let y_offset = j * unitHeight;

        handleFlip(clone, flipCol, flipRow);

        clone.position = new paper.Point(baseShape.position.x + x_offset, baseShape.position.y + y_offset);
        paper.project.activeLayer.addChild(clone); // Add the cloned shape to the Paper.js project
      }
    }
  }

  // 🪟 Function to debounce events
  function debounce(func, delay) {
    let debounceTimer;
    return function() {
      const context = this;
      const args = arguments;
      clearTimeout(debounceTimer);
      debounceTimer = setTimeout(() => func.apply(context, args), delay);
    };
  }

  // 🪟 Event listener for window resize
  window.addEventListener('resize', debounce(resizeCanvas, 250));

  // 🔨 Initialization
  window.onload = function () {
    paper.setup(CTFCanvas);
    resizeCanvas();

    paper.view.onFrame = function (event) {
      updateGenerativeAttributes();
      paper.project.clear();
      drawBackground();
      drawTessellationShape();
    };
  };
