class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}
let R;
let particles = [];
let colors = [];
let parameters = [];


function setup() {
  R = new Random();
  createCanvas(windowWidth, windowHeight);

  let seed = R.random_int(0, 99999);
  randomSeed(seed);
  noiseSeed(seed);

  background(R.random_num(0, 165), R.random_num(0, 165), R.random_num(0, 165));

  for (let i = 0; i < 2000; i++) {
    particles.push([R.random_num(0, width), R.random_num(0, height)]);
  }

  if (parameters.length === 0) {
    for (let i = 0; i < 4; i++) {
      parameters.push(R.random_num(0, 1) + R.random_choice([1, -2]));
    }
  }

  colorMode(HSL, 100);
  let mean = R.random_num(0, 100);
  for (let i = 0; i < 10; i++) {
    colors.push(abs(mean + R.random_num(-10, 10)) % 100);
  }
}

function draw() {
  translate((width - height) / 2, 0);
  for (let i = 0; i < particles.length; i++) {
    let nextParticle = next(particles[i][0], particles[i][1]);
    let x = map(nextParticle[0], -3, 3, 0, height);
    let y = map(nextParticle[1], -3, 3, 0, height);
    fill(colors[i % colors.length], 90, 40);
    noStroke();
    circle(x, y, 1);
    particles[i] = nextParticle;
  }
}

function next(x, y) {
  let nx = sin(parameters[0] * y) + parameters[2] * cos(parameters[0] * x);
  let ny = sin(parameters[1] * x) + parameters[3] * cos(parameters[1] * y);
  return [nx, ny];
}

