function draw() {
  background(R.random_num(185, 255), R.random_num(185, 255), R.random_num(185, 255));
  
  push();
  translate(width / 2, height / 2);
  let ratio = R.random_num(0.3, 0.7);
  let angle_num = R.random_int(3, 6);
  let rn = width / 2 * sqrt(2);
  
  let isWhiteDrawing = R.random_bool(0.5);
  
  for (let rMax = rn; rMax > rn / 5; rMax -= rn / 15) {
    rotate(((rMax / rn) * 360) / 3);
    for (let i = 0; i < 10000; i++) {
      let bool = R.random_bool(0.5);
      let angle =
        (R.random_int(0, angle_num - 1) * 360) / angle_num +
        ((recursiveRandom(3, bool, R) - 0.5) * 360) / angle_num / 2;
      let n = recursiveRandom(R.random_num(0, 5), bool, R);
      let r = rMax * n;
      let x = cos(angle) * r * (bool ? 1 - ratio : 1 + ratio);
      let y = sin(angle) * r * (bool ? 1 - ratio : 1 + ratio);
      
      push();
      translate(x, y);
      shearX(sin(angle * 4 + (360 * r) / rMax) * 89.99);
      shearY(cos(angle * 4 + (360 * r) / rMax) * 89.99);
      
      if (isWhiteDrawing) {
        stroke(255);
      } else {
        stroke(0);
      }
      
      strokeWeight(3 - abs(n - 1) * 3);

      let shadowColor = color(random(255));
      drawingContext.shadowColor = shadowColor;
      drawingContext.shadowBlur = random(0, 150);
      drawingContext.shadowOffsetX = 0;
      drawingContext.shadowOffsetY = random(0, 50);

      point(0, 0);
      pop();
    }
  }
  pop();
}
function recursiveRandom(count, bool = true, R) {
  let n = 1;
  while (count > 0) {
    n *= R.random_dec();
    count--;
  }
  return bool ? 1 - n : 1 + n;
}

