let R;

function setup() {
  createCanvas(windowWidth, windowHeight);
  R = new Random();
  noLoop();

  seed = R.random_int(0, 99999);
  randomSeed(seed);
  noiseSeed(seed);

  let rotationValues = [12, 10, 5, 6, 20];
  let randomRotation = R.random_choice(rotationValues);

  let baseImg = createGraphics(windowWidth, windowHeight);
  baseImg.background(color('#011114'));
  baseImg.stroke(color('#F5F1ED'));
  baseImg.push();
  baseImg.rotate(PI / randomRotation);

  for (let i = 0; i < 200; i++) {
    let agent = new RiftAgent(windowWidth * R.random_num(-0.5, 1.5), windowHeight * R.random_num(-1, 1.5));
    agent.show(baseImg);
  }

  baseImg.fill(color('#F5F1ED'));
  baseImg.noStroke();
  for (let i = 0; i < 200; i++) {
    baseImg.circle(baseImg.width * R.random_num(-0.2, 1.2), baseImg.height * R.random_num(-0.2, 1.2), R.random_num(2, 10));
  }
  baseImg.pop();
  image(baseImg, 0, 0);

  strokeWeight(0.5);
  let img = baseImg.get();
  for (let i = 0; i < 500; i++) {
    drawGroupParticle(windowWidth * R.random_num(-0.1, 1.1), windowHeight * R.random_num(-0.1, 1.1), img);
  }
}



class RiftAgent {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.baseY = y;
    this.posPrev = this.pos.copy();

    this.size = 2 + 24 * pow(random(), 8);
    this.length = sqrt(this.size) * 1.5 * (30 + 150 * pow(random(), 2));
    this.curLength = 0;
    this.dir = random([-1, 1]);
  }

  show(baseImg) {
    noiseSeed(floor(random(10000)));
    while(this.curLength < this.length) {
      this.move();
      this.drawProgress(baseImg);
      this.curLength += 1;
    }
  }

  move() {
    let yOffset = (noise(0.005 * (this.curLength + 1)) * 2 - 1) * this.size * sq(sin(PI * this.curLength / this.length)) * 1;
    this.posPrev = this.pos.copy();
    this.pos = createVector(this.pos.x + this.dir, this.baseY + yOffset);
  }

  drawProgress(baseImg) {
    baseImg.strokeWeight(this.size * sq(sin(PI * this.curLength / this.length)));
    baseImg.line(this.posPrev.x, this.posPrev.y, this.pos.x, this.pos.y);
  }
}

function drawGroupParticle(x, y, img) {
  let centerPos = createVector(x, y);
  let rad = random(20, 50);
  let numParticles = sq(rad) * PI * 0.01 * random(1, 8);
  let baseLength = 40 + 120 * pow(random(), 2);
  let dir = random([0, PI]);

  for(let p = 0; p < numParticles; p++) {
    let distCenter = rad * sqrt(random());
    let angCenter = random(TAU);
    drawParticle(centerPos.x + distCenter * cos(angCenter), centerPos.y + distCenter * sin(angCenter), baseLength, dir, img);
  }
}

function drawParticle(x, y, baseLength, dir, img) {
  let lineLength = baseLength * random(0.05, 1.5);
  let lineAng = 13/16 * TAU + dir;
  let lineDash = {length: 0, array: []};
  while(lineDash.length < lineLength) {
    let nextVal;
    if(lineDash.array.length % 2 == 0) {
      nextVal = random(2, 8);
    } else {
      nextVal = random(6);
    }
    lineDash.array.push(nextVal);
    lineDash.length += nextVal;
  }
  drawingContext.setLineDash(lineDash.array);

  stroke(img.get(x, y));
  line(x, y, x + lineLength * cos(lineAng), y + lineLength * sin(lineAng));
}

