function setup() {
  R = new Random();
  let seed = R.random_int(0, 99999);
  randomSeed(seed);
  noiseSeed(seed);

  createCanvas(windowWidth, windowHeight, WEBGL);
  radius = min(width, height) * 0.6;
  numShapes = colorPalette[0].length + 1;

  selectedPalette = colorPalette[R.random_int(0, colorPalette.length - 1)];
  yellowStroke = selectedPalette[R.random_int(0, selectedPalette.length - 1)];

  pixelDensity(1);
  background(selectedPalette[R.random_int(0, selectedPalette.length - 1)]);

  for (let i = 0; i < numShapes; i++) {
    rotationVecs.push(createVector(0, 0, PI * i / numShapes));
  }

  for (let i = 0; i < numPoints; i++) {
    points.push(new MovingPoint());
  }

  drawArtwork();
  let img = get();
  img = apply_color_glitch(img, 20);
  image(img, -width / 2, -height / 2);
  noLoop();
