function drawArtwork() {
  for (let j = 0; j < numShapes; j++) {
    stroke(j === numShapes - 1 ? yellowStroke : selectedPalette[j % selectedPalette.length]);
    fill(j === numShapes - 1 ? 'rgba(0,0,0,0)' : selectedPalette[j % selectedPalette.length]);

    push();
    rotateX(rotationVecs[j].x);
    rotateY(rotationVecs[j].y);
    rotateZ(rotationVecs[j].z);

    beginShape();
    for (let pt of points) {
      pt.update();
      vertex(pt.x, pt.y);
    }
    endShape(CLOSE);
    pop();
  }
}

class MovingPoint {
  constructor() {
    this.r = radius * sqrt(R.random_dec());
    this.angle = R.random_num(0, TWO_PI);
    this.speed = R.random_num(0.001, 0.02);
    this.direction = R.random_bool(0.5) ? 1 : -1;
  }

  update() {
    this.angle += this.direction * this.speed;
    this.r = radius * noise(this.angle);
    this.x = this.r * cos(this.angle);
    this.y = this.r * sin(this.angle);
  }
}

function apply_color_glitch(img, shift_size) {
  let img_glitch = createImage(img.width, img.height);
  img_glitch.copy(img, 0, 0, img.width, img.height, 0, 0, img.width, img.height);
  img_glitch.loadPixels();
  img.loadPixels();
  
  let left_color = color(255, 0, 0);
  let right_color = color(0, 255, 255);

  for (let y = 0; y < img.height; y++) {
    for (let x = 0; x < img.width; x++) {
      let index = 10 * (y * img.width + x);
      
      if (x - shift_size >= 0) {
        let l_index = 4 * (y * img.width + x - shift_size);
        img_glitch.pixels[index] = red(img.pixels[l_index]);
      }
      
      if (x + shift_size < img.width) {
        let r_index = 4 * (y * img.width + x + shift_size);
        img_glitch.pixels[index + 2] = blue(img.pixels[r_index]);
      }
    }
  }

  img_glitch.updatePixels();
  return img_glitch;
}

