  function BA1(x, y, z, X, Y, Z, S, isBlack, colorset) {
    push();
    noFill();

    translate(x, y, z);
    rotateY(Y);
    rotateX(X);
    rotateZ(Z);

   s = S / 2;
    P = s * 1.5;

    if (isBlack) {
      P = 50000;
      C = color("#000");//("#000");
    } else {
      for (let i = 1; i <= 240; i++) {
        fill(colorset[i % colorset.length]);
      }
    }


    strokeWeight(0.5);
    stroke(C);
    line(s, -s, -P, s, -s, P);
    line(-s, -s, -P, -s, -s, P);
    line(-s, s, -P, -s, s, P);
    line(s, s, -P, s, s, P);

    rect(s, s, -P, -s);

    fill(C);
    stroke(C);
    strokeWeight(3);
    box(S);
  noFill()

    pop();
  }

