  class Rect {
    constructor(_theta, _j) {
      this.k_1 = 1;
      this.k_2 = 5;
      this.i = random(2, 3); // this influence the size of the rects
      this.theta = _theta;
      this.angle = _j;
      this.hexX;
      this.hexY;
    }

    display() {
      this.theta+=0.005;
      this.hexX = (width / 4) * cos(this.theta*3);
      this.hexY = (width / 10) * sin(this.theta);
      rotateY(radians(this.angle * 17));
      rotateX(radians(this.angle * 24));
      //noStroke();
      //stroke(255);
      line(0, 0, this.hexX, this.hexY);

      push();
      translate(this.hexX+cos(this.theta*10)*50, this.hexY);
      rotateZ(radians(this.theta*5));
      //rotateZ(this.theta*5);
      rotateY(PI / 2);
      if (this.angle % 30 < 10) {
        fill(
          205 + sin(radians(this.k_1 * 100)) * 180,
          185 + sin(radians(this.k_2 * 80)) * 50,
          185 + sin(this.k_1 * 80) * 140,
          180 + sin(radians(360 - this.angle * 100)) * 55
        );
      } else if (this.angle % 30 >= 10 && this.angle % 30 < 20) {
        fill(
          105 + cos(radians(360 - this.k_2 * 100)) * 50,
          225 + cos(radians(this.k_1 * 800)) * 50,
          225 + sin(this.k_2 * 30) * 140,
          150 + cos(radians(this.angle * 100)) * 105
        );
      } else {
        fill(
          150 + cos(radians(this.k_1 * 200)) * 80,
          225 + cos(radians(this.k_1 * 800)) * 50,
          255,
          125 + cos(radians(this.k_1 * 100)) * 105
        );
      }
      beginShape();
      vertex(this.i, 0);
      vertex(this.i / 2, this.i * sin(30));
      vertex(-this.i / 2, this.i * sin(30));
      vertex(-this.i, 0);
      vertex(-this.i / 2, -this.i * sin(30));
      vertex(this.i / 2, -this.i * sin(30));
      endShape(CLOSE);
      pop();
    }
    move() {
      this.k_1 += 0.0015;
      this.k_2 += 0.0003;
      rotateY(this.k_1);
      //console.log(this.k);
    }
  }

