    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
        push(); // شروع تنظیمات جدید برای هرمربع
        translate(i * step + margin/2, j * step + margin/2); // تغییر مختصات به مرکز مربع
