          let shape1Functions = shapes1[shapeIndex];
          for (let k = 0; k < shape1Functions.length; k++) {
            shape1Functions[k]();
          }
        }

        pop(); // پایان تنظیمات جدید برای هر مربع
      }

    }
  }


