  let A2Bv = getDir(0.5);
  let A2Bl = A2Bv.x;
  let A2Br = A2Bv.y;
  let A2Bts = random(height * 0.05, height * 0.075);
  let A2Bnb = random(2, 4);
  strokeWeight(A2Bts);
  line(0, 0, 0, -A2Bos);
  translate(0, -A2Bos);
  multiBranch(A2Bbs, A2Bl, A2Br, A2Bts, A2Bnb);
  A2BdrawTree = false;
  }
  function getDir(A2Bp = 0.1) {
  let A2Br = random(0, 1);
  let A2Blp = 1 - A2Bp;
  let A2Bl = false;
  let A2Bri = false;
  if (A2Br >= A2Blp) {
  A2Bl = true;
  } else if (A2Br <= A2Bp) {
  A2Bl = true;
  } else {
  A2Bl = true;
  A2Bri = true;
  }
  return createVector(A2Bl, A2Bri);
  }
  function drawBranch(A2Bth, A2Bh, A2Bthres, A2Bts, A2Bbp) {
  A2Bts *= random(0.6, 0.9);
  strokeWeight(A2Bts);
  push();
  rotate(-A2Bth);
  rotateZ(-th); 
    line(0, 0, 0, 0, -h, 0);
  translate(0, -A2Bh);
  push(); // شاخه دوم
  rotate(radians(30));
  pop();
  let A2Bv = getDir();
  let A2Bl = A2Bv.x;
  let A2Br = A2Bv.y;
  branch(A2Bh, A2Bl, A2Br, A2Bthres, A2Bts, A2Bbp);
  pop();
  }
  function branch(A2Bh, A2Bl, A2Br, A2Bthres = 6, A2Bts = 3, A2Bbp = []) {
  let A2Ba = random(-30, 30);
  let A2Bs = 6;
  let A2Bt = radians(A2Ba);
  A2Bh *= random(0.7, 1);
  let A2Bi = A2Bbp.length;
  A2Bbp[A2Bi] = createVector(A2Bbp[A2Bi - 1].x + A2Bh * cos(A2Bt), A2Bbp[A2Bi - 1].y - A2Bh * sin(A2Bt));
  if (A2Bbp[A2Bi].x > 0 && A2Bbp[A2Bi].y > 0) {
  if (A2Bh > A2Bthres) {
  if (A2Br) {
  drawBranch(A2Bt, A2Bh, A2Bthres, A2Bts, A2Bbp);
  }
  if (A2Bl) {
  drawBranch(-A2Bt, A2Bh, A2Bthres, A2Bts, A2Bbp);
  }
  } else {
  stroke(120);
  strokeWeight(random(0, 4));
  line(0, 0, random(0, 20), random(0, 50));
  translate(0, -A2Bh);
  }
  }
  }
  function multiBranch(A2Bbs, A2Bl, A2Br, A2Bts = random(100, 600), A2Bnb = random(30, 200)) {
  for (let i = 0; i <= A2Bnb; i++) {
  const A2Bos = (20, 200);
  let A2Bbp = [];
  A2Bbp[0] = createVector(2000 / 2, 1000 - A2Bos);
  branch(A2Bbs, A2Bl, A2Br, A2Bthres = random(10, 20), A2Bts = A2Bts, A2Bbp);
  }
  }





