function draw() {
  for(let y = -300; y < height; y += R.random_num(80, 150)) {
    curtains.push(new Curtain(y))
  }
  
  for(let curtain of curtains) {
    curtain.paint()
    curtain.drawLine()
  }
}

class Curtain {
  constructor(y) {
    this.y = y;
    this.angle = R.random_num(-PI / 4, PI / 4);
    this.startY = this.y + width / 2 * tan(this.angle);
    this.endY = this.y - width / 2 * tan(this.angle);
  }

  paint() {
    noStroke();
    fill(getCol());
    beginShape()
		vertex(0, this.startY)
		vertex(width, this.endY)
		vertex(width, height+50)
		vertex(0, height+50)
		endShape(CLOSE)
		
		let strokeWidth = random(4, 15)
		// strokeWeight(strokeWidth)
		let gapBase = random(strokeWidth*2, strokeWidth*5)
		stroke(color('#260B04'))
		strokeCap(ROUND)
		
		for(let x = random(0, 20); x<width; x+=random(gapBase*0.5, gapBase*2)) {				
			for(let y = map(x, 0, width, this.startY, this.endY); y<height*1.5; y+=2) {
				strokeWeight(strokeWidth*map(noise(x * 0.01, y * 0.01), 0, 1, 0.5, 1.5))
				line(x, y, x, y+2)
			}
		}
	}

	drawLine() {
		stroke(color('#260B04'))
		strokeWeight(20)
		strokeCap(PROJECT)
		line(0, this.startY, width, this.endY)
	}
}

