//--------Alphabet
function isInside(n,maxn){
  let inside = false;
  if(n>=0 && n<maxn){inside = true;}
  return inside;
}
function isNotParal(npx, npy, axis){
  let imcool = true;
  let p1x, p1y, p2x, p2y;//parallels
  if(axis == 1){//vertical
    p1x = p2x = npx;
    p1y = npy-1;
    p2y = npy+1;
  } else if(axis == 0){//horizontal
    p1y = p2y = npy;
    p1x = npx-1;
    p2x = npx+1;
  }
  if(isInside(p1x,pxW) && isInside(p1y,pxH) && gridOnOff[p1x][p1y]==1){
    imcool = false;
  }
  if(isInside(p2x,pxW) && isInside(p2y,pxH) && gridOnOff[p2x][p2y]==1){
    imcool = false;
  }
  return imcool;
}
function lineLineIntersect(x1,y1,x2,y2, x3,y3,x4,y4) {
  den = 1 / ((x1-x2) * (y3-y4) - (y1-y2) * (x3-x4));
  t = ((x1-x3) * (y3-y4) - (y1-y3) * (x3-x4)) * den;
  u = -((x1-x2) * (y1-y3) - (y1-y2) * (x1-x3)) * den;

  if (t > 0 && t < 1 && u > 0 && u < 1) {
    hitPoint = createVector(0,0);
   hitPoint.x = x1 + t * (x2 - x1);
   hitPoint.y = y1 + t * (y2 - y1);
   return true;
  }
  else return;

}

//--------RANDOMS
let rrs, rroff;
let rrsL = 0;
function initRandoms(){
//Init randoms array 
  rrsL = floor(R.random_num(40000,50000));//I can reset whenever
  rroff = floor(R.random_dec()*rrsL);
  rrs = [];
  for(let r=0; r<rrsL; r++){
    rrs.push(R.random_dec());
  }
}
function rr(){
  rroff++;
  rrvalue = rrs[rroff%rrsL];
  return rrvalue;
}
function rr1(){
  rroff++;
  rrvalue = (rrs[rroff%rrsL]-0.5)*2;
  return rrvalue;
}
function rr2(a,b){
  rroff++;
  rrvalue = a+rr()*(b-a);
  return rrvalue;
}
function rri(a){
  rroff++;
  rrvalue = floor(rr()*a);
  return rrvalue;
}
function rrarr(myArr){
  let temprs = [];
  for(let r = 0; r<myArr.length; r++){
    rroff++;
    newval = rr(rroff);
    temprs.push(newval);
  }
  volta = -1;
  maxV = Math.max(...temprs);
  tempV = maxV+1;
  while(tempV!=maxV){
    volta++;
    tempV = temprs[volta%temprs.length];
  }
  let chosen = myArr[volta%myArr.length];
  return chosen;
}
//-----
function balancedChoice(opts,pct){
  balanced = [];
  for(let o=0; o<opts.length; o++){
    for(let t=0; t<pct[o]; t++){
      balanced.push(opts[o]);
    }
  }
  theOne = R.random_choice(balanced);  
  return theOne;
}
//-----
function shuffleArray(tarray) {
array = [...tarray];
  for (let i = array.length - 1; i > 0; i--) {
      let j = Math.floor(R.random_dec() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
  }
  return(array)
}
function windowResized() {
  w = floor(min(windowWidth, windowHeight/ratio));
  h = floor(w*ratio);  
  resizeCanvas(w,h, false);

  image(collect,-w/2,-h/2,w,h);
}
function keyPressed() {
  if (key === 's') {
    save(collect, mySeed+".png");
  }
  if (key === 'o') {
    save( mySeed+".png");
  }
}

