function calcCellsGrid(stretch,cols){
  phase = rr()*10000;
	cellsGrid = [];
	distortX = rr2(1.2,1.7);
 if(stretch=='spread_sharp' || stretch=='spread_flashy'){
    if(stretch=='spread_flashy' && cols>60){distortX=1;}
    rows = cols;
	distortX = rr2(0.7,1.2);
	distortY = rr2(0.7,1.2);
	  v0 = createVector(cols/2,rows/2);
	  for(let c=0; c<cols; c++){
	    for(let r=0; r<rows; r++){
	      v1 = createVector(c*distortX,r*distortY);
	      d = v0.dist(v1)+noise(c,r)*floor(cols/5);//spiky
	      val = (d<(cols/3))*1;
				if (val==1 && (c%2)==(r%2)){
					v2 = createVector(c-v0.x,r-v0.y);
		      cellsGrid.push(v2)
				}
			}
		}
	} 

  else if(stretch=='build_sharp'){//spikes
    rows = cols;
	  v0 = createVector((cols-1)/2,(rows-1)/2);
	  for(let c=0; c<cols; c++){
	    for(let r=0; r<rows; r++){
	      v1 = createVector(c,r);
	      d = v0.dist(v1)+noise(phase+c*0.05,r*0.05)*floor(cols/5);//smooth
	      d = v0.dist(v1)+noise(phase+c*0.5,r*0.5)*floor(cols/5);//spiky
	      val = (d<(cols/2))*1;
				if (rr()>0.9){
					v2 = createVector(c-v0.x,r-v0.y);
		      cellsGrid.push(v2)
				}
			}
		}
	}
return cellsGrid;	
}

