const vert =  `#version 300 es
precision highp float;

uniform mat4 uModelViewMatrix;
uniform mat4 uProjectionMatrix;
uniform mat3 uNormalMatrix;

in vec3 aPosition;    // Vertex position
in vec2 aTexCoord;    // Vertex texture coordinate
in vec3 aNormal;      // Vertex normal

out vec3 pos;
out vec3 normalInterp;
out vec2 vTexCoord;
out vec4 pos4;
void main(){
		vTexCoord = aTexCoord;
    gl_Position = uProjectionMatrix * uModelViewMatrix * vec4(aPosition, 1.0);
    pos4 = uModelViewMatrix * vec4(aPosition, 1.0);

    normalInterp = uNormalMatrix * aNormal;
    pos = vec3(pos4) / pos4.w;
}
`;
const frag =  `#version 300 es
precision highp float;

//--for light
in vec3 pos;
in vec3 normalInterp;
in vec2 vTexCoord;
in vec4 pos4;

//--bkg
uniform vec4 bkgColor;
uniform float objType;
//--for skin
uniform float notBkg;
uniform sampler2D img;
uniform vec2 offPos;
uniform vec2 offskl;
uniform int imgOnOff;

//--for threshold
uniform vec2 u_resolution;
uniform float phase;
uniform sampler2D originalImg;
uniform int palLength;
uniform int thLength;
uniform vec4 pal[6];
uniform float th[5];
vec2 u;

//--more for light
uniform vec3 lightPos;
const vec3 ambientColor = vec3(0.1);
const vec3 diffuseColor = vec3(.6);
const vec3 specColor = vec3(.2);

out vec4 finalColor;

//---NOISE TEXTURE
#define hash(p) (2. * fract(sin((p)*mat2(127.1, 311.7, 269.5, 183.3)) * 43758.5453123) - 1.)
#define func(P) perlin(.5*(P))
#define grad(x, y) dot(hash(i + vec2(x, y)), f - vec2(x, y))

float noise(vec2 p){
    vec2 i = floor(p);
		vec2 f = fract(p);
		// u = f*f*(3. - 2.*f);
		u = f*f*f*( 10. +f*(6.*f-45.));
    return mix(mix(grad(0., 0.), grad(1., 0.), u.x),
               mix(grad(0., 1.), grad(1., 1.), u.x), u.y);
}

float perlin(vec2 U) {
  float v = 0., s = .5;
  mat2 m = mat2(1.2, 0.8, -0.8, 1.2);
	for( int i=0; i<3; i++ )
			s /= 2.;
			U *= m;
      v += s * noise( U ); 
  return v;
}
//---------GRANULATED BKG
float rows = 200.0;
float noiseSk = 2.;
float random(in vec2 st){
    return fract(sin(dot(st.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noize (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);
    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f*f*(3.0-2.0*f);// Cubic Hermine Curve.  Same as SmoothStep()
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}
float map(float value, float min1, float max1, float min2, float max2){
    float perc = (value - min1) / (max1 - min1);
    // Do the same operation backwards with min2 and max2
    value = perc * (max2 - min2) + min2;
    return value;
}

vec3 noisedMatrix(in vec2 st){
    vec2 ipos = floor(st*rows);
    vec2 fpos = fract(st*rows);
    float shiftC = 0.87;

    float csize = noize((phase*1000.+notBkg*1000.)+noiseSk*ipos/rows)*20.;
    csize += random(vec2(notBkg));
    vec2 center = .5-fpos+ shiftC*(random(ipos) -0.5);
    float glow = dot(center,center)*csize;
    glow = min(glow,1.2);
    glow = abs(2.2-max(glow,0.7));
    // glow = 0.;
    float bigN = noize((phase+notBkg*1000.)+st*1.82)*0.2;
    bigN = abs(.5-noize((phase+notBkg*1000.)+st*1.82)*0.4);
    bigN += random(vec2(notBkg));
    // bigN = 0.;

    float quoc = 2.- 0.7*(2.*fract(objType/2.));

//Only Purewhite (in palette Ink) has green=1.0    
    if(pal[2].g==1.){return vec3((glow+bigN)/1.3);}
    else{return vec3((glow+bigN)/quoc);}
}

//--------CALCULATIONS:
void main() {

//--LIGHT COLOR
	vec3 normal = normalize(normalInterp);
	vec3 lightDir = normalize(lightPos - pos);

	float lambertian = max(dot(lightDir,normal), 0.0);
	float specular = 0.0;

	if(lambertian > 0.0) {
		vec3 viewDir = normalize(-pos);
		vec3 halfDir = normalize(lightDir + viewDir);
		float specAngle = max(dot(halfDir, normal), 0.0);
		specular = pow(specAngle, 32.0);
	}

	vec4 lightColor = vec4(ambientColor + lambertian * diffuseColor + specular * specColor, 1.0);

//--IMG COLOR
  vec4 imgColor = texture(img, (vTexCoord+offPos)*offskl);
	vec4 noImg = vec4(1.);// neutral for multiplication with lightColor
	vec4 hasImg[2];
	hasImg[0] = noImg;
	hasImg[1] = imgColor;

//-----MIX LIGHT+SKIN---
	float bri = (lightColor * hasImg[imgOnOff]).r;
  bri*=notBkg;

//--THR COLORS
    vec4 thresC = vec4(0.);
    vec4 c1  = pal[0];
    vec4 c2  = pal[1];
    float thr = 0.0;
    int myTh = 0;
    float minn = (th[0] + th[1])/2.0;
    float maxx = (th[1] + th[2])/2.0;
    if(bri<minn){
    	thr = th[0];
    	c1 = pal[0]; c2 = pal[1];
    } 
    else {
      for( int i=1; i<4; i++ ){
      	minn = (th[i-1] + th[i])/2.0;
      	maxx = (th[i] + th[i+1])/2.0;
      	if(bri>=minn && bri<maxx){
      		thr = th[i];
      		c1 = pal[i]; c2 = pal[i+1];
          myTh = i;
      	}
      }
      if(bri>=maxx){
      	thr = th[4];
      	c1 = pal[4]; c2 = pal[5];
        myTh = 4;
      }
    } 
    float aa = fwidth(bri);
    // aa = 0.0;
    // aa*=2.;
    // aa=1.0;
    float mixAmount = smoothstep(thr - aa, thr + aa, bri);
    thresC = mix(c1, c2, mixAmount);


//--MAD TEXTURE COLOR
    vec2 R = u_resolution.xy,
		 U = gl_FragCoord.xy / R.y,
     P = (.05 + 0.005*thr) * U - 4.1*vec2(30., 28.1*phase); //ORIGINAL mini

    vec4 texturedC = vec4(1.);

    float l, dl, f = func(P), df;
    l = exp2(floor(log2(1./22./fwidth(f))));
    l *=thr/2000.0;//Este ELIMINA las amebas!!
    dl = fract(log2(1./22./fwidth(f)));

    float maxB = 0.5;
    float ta = sin(450.*l*f);
    float tb = 0.8*sin(100.*l*f);
    float tMix = mix(ta, tb, dl);

    float tMult = 0.2;
    texturedC = vec4(vec3(tMult * tMix),1.0);

vec4 mixColor = vec4(vec3(thresC.rgb + texturedC.rgb),1.0);

//---GRANULATED BKG
    vec2 st = gl_FragCoord.xy/u_resolution.xy;
    vec3 grainC = noisedMatrix(st);
	vec4 possibleCs[3];
	possibleCs[0] = bkgColor;
	possibleCs[1] = vec4(0.85);//PALETA ATENUADA!!

	vec4 crossColor = (possibleCs[int(notBkg)]);
  vec4 granulatedColor = vec4( vec3(grainC)*crossColor.rgb , 1.0);

//----------FINAL COLOR
  vec4 bkgMix = texturedC + granulatedColor;
  vec4 foreMix = thresC * granulatedColor;

	possibleCs[0] = foreMix;
  imgColor.r = max(imgColor.r,0.9);//watermark-like graffiti for bkg
  imgColor.g = max(imgColor.g,0.9);//watermark-like graffiti for bkg
  imgColor.b = max(imgColor.b,0.9);//watermark-like graffiti for bkg
	possibleCs[2] = granulatedColor*imgColor;//bkg
	possibleCs[1] = bkgMix;//bkg

	finalColor = (possibleCs[int(objType)]);

	finalColor = sqrt(finalColor);

}
