    rev.push(sw);
    sw = !sw;
  }
  startTime = millis();
}

function draw() {
  translate(0.5 * width, 0.5 * height);

  for (var i = 0; i < ai.length; i++) {
    var radiusInt = 100 + i * 50;
    var radiusExt = radiusInt + 50;
    drawPointsRing(1000, radiusInt, radiusExt, ai[i], rev[i]);
  }

  drawRose(2000, 0, 150, 0);

  if (!hasStopped && millis() - startTime >= 2800) {
    noLoop();
    hasStopped = true;
    granulateChannels(60);
  }

  if (hasStopped) {
    for (var i = 0; i < ai.length; i++) {
      var radiusInt = 100 + i * 50;
      var radiusExt = radiusInt + 50;
      for (var j = 0; j < 1000; j++) {
        var r = random(radiusInt, radiusExt);
        var a = random(ai[i], ai[i] + TAU);
        var x = r * cos(a);
        var y = r * sin(a);
        translate(x, y);
        customEffect(0, 0, 10);
        translate(-x, -y);
      }
    }
  }
}

function drawPointsRing(num, radiusInt, radiusExt, initAngle, reversed) {
  for (var n = 0; n < num; n++) {
    var r = R.random_num(radiusInt, radiusExt);
    var a = R.random_num(initAngle, initAngle + TAU);
    var p = (a - initAngle) * 1.0 / TAU;
    p = reversed ? 1.0 - p : p;
    if (R.random_dec() < p) {
      var x = r * cos(a);
      var y = r * sin(a);
      stroke(0);
      point(x, y);
    }
  }
}

function drawRose(num, radiusInt, radiusExt, initAngle) {
  for (var i = 0; i < num; i++) {
    var n = 5;
    var d = 8;
    var r = R.random_num(radiusInt, radiusExt);
    var a = R.random_num(initAngle, initAngle + d * TAU);
    var p = r / 300;
    var k = n / d;
    if (R.random_dec() < p) {
      var x = r * cos(k * a) * cos(a);
      var y = r * cos(k * a) * sin(a);
      stroke(0);
      point(x, y);
    }
  }
}

function customEffect(_x, _y, _r) {
  push();
  blendMode(MULTIPLY);
  let ctx = drawingContext;
  let gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, _r);
  let flip = R.random_dec() < 0.67;
  for (let i = 0; i <= 1; i += 0.001) {
    let colorVal = pow(i, 50 * sqrt(_r / 600)) * 255;
    if (flip) colorVal = 255 - colorVal;
    gradient.addColorStop(i, color(colorVal));
  }
  ctx.fillStyle = gradient;
  circle(_x, _y, _r * R.random_num(0.8));
  fill(0);
  pop();
}

function granulateChannels(amount) {
  loadPixels();
  const d = pixelDensity();
  const pixelsCount = 4 * (width * d) * (height * d);
  for (let i = 0; i < pixelsCount; i += 4) {
    const grainAmount = R.random_num(-amount, amount);
    pixels[i] = pixels[i] + grainAmount;
    pixels[i + 1] = pixels[i + 1] + grainAmount;
    pixels[i + 2] = pixels[i + 2] + grainAmount;

  }
  updatePixels();
}

