/*
 * Cria uma grid com elementos que rodam e se posicionam com um certo offset 
 */
function createGrid(px, py, sizx, sizy, cols, rows) {
  xOffSize = 0;
  yOffSize = 0;
  xOffOffset = 0;
  yOffOffset = 0;
  cellWidth = sizx / cols;
  cellHeight = sizy / rows;

  mediumSize = Math.min(cellWidth, cellHeight);

  color_distribution = random(["Random","Noise","Random"])

  ellipseMode(CENTER);
  rectMode(CENTER);

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {

      //Seta um multiplicador aleatorio que pode ser usado depois
      rnd_multiplier = random(0.5, 4)
      shapeSize = (mediumSize * 1.25) * random([1, 1, 1, 1, , rnd_multiplier])

      if (color_distribution == "Random"){
      essa_cor = cor[rnd_int(0, tm_cor)]
      }
      else{
        cor_index = Math.round(map(noise(xOffSize, yOffSize), 0, 1, 0, tm_cor))
        essa_cor = cor[cor_index]
      }

      //Faz um random pra saber se vai ser fill ou stroke
      if (random() > 0.5) {
        noFill();
        stroke(essa_cor);
      } else {
        noStroke();
        fill(essa_cor);
      }

      let x = px + i * cellWidth + cellWidth / 2;
      let y = py + j * cellHeight + cellHeight / 2;

      // Adicionando o deslocamento baseado em um ruído ao y
      y += map(
        noise(xOffOffset, yOffOffset),
        0,
        1,
        -mediumSize * 2,
        mediumSize * 2
      ); // Variamos a amplitude para -100, 100 para tornar o deslocamento mais evidente

      let mappedNoise = map(noise(xOffSize, yOffSize), 0, 1, 0.5, 1.5);

      // Escolhendo a forma
      let shapeChoice = random();

      // Salva a transformação atual da matriz
      push();

      // Translada o sistema de coordenadas para o centro da forma
      translate(x, y);

      // Aplica uma rotação aleatória
      rotate(random(TWO_PI));

      if (shapeChoice < 0.25) {
        // Desenha um quadrado
        scribbleRectangle(0, 0, shapeSize * mappedNoise, shapeSize * mappedNoise);
      } else if (shapeChoice < 0.5) {
        // Desenha um triângulo
        triangle(
          0,
          (-shapeSize * mappedNoise) / 2,
          (-shapeSize * mappedNoise) / 2,
          (shapeSize * mappedNoise) / 2,
          (shapeSize * mappedNoise) / 2,
          (shapeSize * mappedNoise) / 2
        );
      }
      else if (shapeChoice < 0.75) {
        dottedRect(0, 0, shapeSize * mappedNoise, shapeSize * mappedNoise)
      }
      else {
        // Desenha uma elipse
        let ellipseRadius = (shapeSize / 2) * mappedNoise;
        scribbleEllipse(0, 0, ellipseRadius * 2, ellipseRadius * 2);
      }

      // Restaura a transformação anterior da matriz
      pop();

      yOffSize += 0.1;
      yOffOffset += 0.1; // Aumentamos a quantidade que adicionamos para tornar o deslocamento menos suave
    }

    xOffSize += 0.1;
    xOffOffset += 0.1; // Aumentamos a quantidade que adicionamos para tornar o deslocamento menos suave
  }
}

/*
* Cria um quadrado cheio de circulos dentro
*/
function dottedRect(px, py, sizx, sizy) {
  push()
  translate(px - sizx / 2, py - sizy / 2)


  qqtt = Math.min(sizx, sizy) * rnd_btw(55.5, 88)
  if (random() > 0) {
    easeIn = true
  }
  else {
    easeIn = false
  }

  for (let i = 0; i < qqtt; i++) {
    x = random(sizx); // generate a random x position within the canvas width
    if (easeIn) {
      y = easeInCubic(random(1)) * sizy;
    }
    else {
      y = easeOutCubic(random(1)) * sizy;
    }
    point(x, y, 2); // draw a circle at the random x and y position with a diameter of 2
  }
  pop()
}

function scribbleRectangle(x, y, w, h) {

  if (h == undefined) {
    h = w
  }

  offsete = 2
  steps = 25
  // Set the stroke weight
  strokeWeight(1);

  // Begin a new path
  beginShape();

  // Set the first vertex at the position (x, y)
  vertex(x, y);

  // Add a series of vertices along the top edge of the rectangle
  for (let i = 0; i < w; i += steps) {
    let nx = x + i + map(noise(i * 0.1, y * 0.1), 0, 1, -offsete, offsete);
    let ny = y + map(noise(x * 0.1, i * 0.1), 0, 1, -offsete, offsete);
    vertex(nx, ny);
  }

  // Add a vertex at the top-right corner of the rectangle
  vertex(x + w, y);

  // Add a series of vertices along the right edge of the rectangle
  for (let i = 0; i < h; i += steps) {
    let nx = x + w + map(noise(w * 0.1, (y + i) * 0.1), 0, 1, -offsete, offsete);
    let ny = y + i + map(noise((x + w) * 0.1, i * 0.1), 0, 1, -offsete, offsete);
    vertex(nx, ny);
  }

  // Add a vertex at the bottom-right corner of the rectangle
  vertex(x + w, y + h);

  // Add a series of vertices along the bottom edge of the rectangle
  for (let i = 0; i < w; i += steps) {
    let nx = x + w - i + map(noise((w - i) * 0.1, (y + h) * 0.1), 0, 1, -offsete, offsete);
    let ny = y + h + map(noise((x + w) * 0.1, (h + i) * 0.1), 0, 1, -offsete, offsete);
    vertex(nx, ny);
  }

  // Add a vertex at the bottom-left corner of the rectangle
  vertex(x, y + h);

  // Add a series of vertices along the left edge of the rectangle
  for (let i = 0; i < h; i += steps) {
    let nx = x + map(noise(x * 0.1, (y + h - i) * 0.1), 0, 1, -offsete, offsete);
    let ny = y + h - i + map(noise(i * 0.1, (y + h) * 0.1), 0, 1, -offsete, offsete);
    vertex(nx, ny);
  }

  // End the path and draw the shape
  endShape(CLOSE);
}


function scribbleEllipse(x, y, radiuss) {
  let offset = 10; // Define the maximum offset for the noise
  let detail = rnd_btw(0.001, 0.1); // This determines the amount of detail in the noise
  let steps = 100; // The number of points on the circle

  radius = radiuss / 2
  // Set the stroke weight
  strokeWeight(1);

  // Begin a new path
  beginShape();

  // Loop around the circle in increments of TWO_PI/steps
  for (let i = 0; i <= steps; i++) {
    let angle = map(i, 0, steps, 0, TWO_PI - TWO_PI / steps); // Subtract ONE step from TWO_PI to close the circle smoothly

    // Get a noisy radius
    let noisyRadius = radius + offset * noise(i * detail);

    // Convert polar to Cartesian coordinates
    let nx = x + noisyRadius * cos(angle);
    let ny = y + noisyRadius * sin(angle);

    // Set the vertex
    vertex(nx, ny);
  }

  // End the path and draw the shape
  endShape(CLOSE);
}
/* 
 * Cria a classe que coloca os elementos dentro de uma ellipse 
 */

class EllipseGenerator {
  constructor(posX, posY, r_max, ellipseSize, qtt_coisa) {
    // Define position and size
    this.posX = posX;
    this.posY = posY;
    this.ellipseSize = ellipseSize;
    this.r_max = r_max;
    this.qtt_coisa = qtt_coisa;

  }

  generate() {
    push();
    translate(this.posX, this.posY);

    for (let k = 0; k < this.qtt_coisa; k++) {

      let size_mult = random([1, 1, 1, rnd_btw(1.25, 3)])
      let esse_tamanho = this.ellipseSize * size_mult

      let obj = this.formula(int(random(0, 8)), this.r_max);
      let r = obj.value;
      let angle = random(TWO_PI);
      let point_x = cos(angle) * r;
      let point_y = sin(angle) * r;

      let randim = random(0, 100)
      noStroke();
      fill(cor[rnd_int(0, tm_cor)]);
      // Use this.ellipseSize to control size
      if (randim > 75) {
        scribbleEllipse(point_x, point_y, random(10, esse_tamanho));
      }
      else if (randim > 50) {
        push()
        noFill()
        stroke(cor[rnd_int(0, tm_cor)])
        strokeWeight(3)
        rotate(random(2 * PI))
        scribbleRectangle(point_x, point_y, random(10, esse_tamanho));
        pop()
      }
      else {
        push()
        rotate(random(2 * PI))
        stroke(cor[rnd_int(0, tm_cor)])
        dottedRect(point_x, point_y, random(10, esse_tamanho), random(10, esse_tamanho))
        pop()
      }
    }

    pop();
  }

  formula(num, r) {
    let value, str;
    switch (num) {
      case 0:
        value = random() * r;
        break;
      case 1:
        value = (1 - random(random())) * r;
        break;
      case 2:
        value = (1 - random(random(random()))) * r;
        break;
      case 3:
        value = (1 - random(random(random(random())))) * r;
        break;
      case 4:
        value = (1 - sqrt(random())) * r;
        break;
      case 5:
        value = sqrt(random()) * r;
        break;
      case 6:
        value = pow(1 - pow(random(), 10), 10) * r;
        break;
      case 7:
        value = tan(random(TWO_PI)) * r;
        break;
      case 8:
        value = (atan(random(TWO_PI)) * r) / sqrt(2);
        break;
    }
    return {
      value: value,
    };
  }
}

