/*
* MATH FUNCTIONS
*/
function easeOutCubic(x) {
  return 1 - Math.pow(1 - x, 3);
}
function easeInCubic(x) {
  return x * x * x;
}
function easeInOutSine(x) {
  return -(Math.cos(Math.PI * x) - 1) / 2;
}
function gaussian(x) {
  return randomGaussian(0.5, 0.1);
}






function texturinha(px, py, sizx, sizy) {
  push()
  translate(px, py)
  // Cria aqui o retangulos de fundo com tamanho sizx e sizy e posicao mudando a partir do translate
  // O 0,0 coloca ele no ponto 0 pro translate mudar no canvas
  //rect(0,0,sizx,sizy)

  // Criei variavel x e y, pode criar com let x e let y sem problema
  //OBS: dentro de funcao nao precisa declarar com let
  pos = createVector()

  //O gap entre os traçados
  gap = 10
  if (!dark) {
    texturinha_cor = rnd_btw(0, 25)
  }
  else {
    texturinha_cor = rnd_btw(240, 255)
  }
  stroke(texturinha_cor, 25)


  for (let j = 0; j < sizx; j += gap) {

    //Posicao y varia com o loop
    pos.x = j
    // Posicao x é randomica entre 55% da largura e 100% dela
    pos.y = sizy
    strokeWeight(1)
    line(pos.x, 0, pos.x, pos.y)
  }

  //Puxei um for loop pra preencher o retangulo
  for (let i = 0; i < sizy; i += gap * 2) {

    //Posicao y varia com o loop
    pos.y = i * rnd_btw(0.95, 1.05)
    // Posicao x é randomica entre 55% da largura e 100% dela
    pos.x = sizx
    strokeWeight(0.25)
    line(0, pos.y, pos.x, pos.y)
  }
  pop()
}

function customLine(x1, y1, x2, y2) {
  // Determine the distance and angle between the two points
  noFill()
  distance = dist(x1, y1, x2, y2);
  angle = atan2(y2 - y1, x2 - x1);

  // Set the frequency and amplitude of the Perlin noise offset for position and size
  noiseFreq = random(0.05);
  noiseAmp = random(6);
  sizeAmp = 1;

  // Draw the line as a series of rectangles with Perlin noise offset for position and size
  for (let i = 0; i < distance; i += 2) {
    x = x1 + cos(angle) * i;
    y = y1 + sin(angle) * i;

    rectWidth = map(i, 0, distance, 3, 0.001);

    // Add a Perlin noise offset to the position and size of each rectangle
    noiseVal1 = noise(x * noiseFreq, y * noiseFreq);
    noiseVal2 = noise(x * noiseFreq * 2, y * noiseFreq * 2);
    x += map(noiseVal1, 0, 1, -noiseAmp, noiseAmp);
    y += map(noiseVal1, 0, 1, -noiseAmp, noiseAmp);
    rectWidth += map(noiseVal2, 0, 1, -sizeAmp, sizeAmp);

    if (random() > 0.1) {
      if (random() > 0.05) {
        ellipse(x, y, rectWidth * random(0.75, 1.25));
      } else {
        ellipse(
          random(x * 0.98, x * 1.02),
          random(y * 0.98, y * 1.02),
          rectWidth * random(1.25, 2)
        );
      }
    }
  }
}

function granulate(gA) {
  loadPixels();
  let d = pixelDensity();
  let halfImage = 4 * (width * d) * (height * d);
  for (let i = 0; i < halfImage; i += 4) {
    grainAmount = random(-gA, gA);
    pixels[i] = pixels[i] + gA;
    pixels[i + 1] = pixels[i + 1] + grainAmount;
    pixels[i + 2] = pixels[i + 2] + grainAmount;
    pixels[i + 3] = pixels[i + 3] + grainAmount;
  }
  updatePixels();
}

