function calculateFeatures() {

  colors = R.random_choice(palettes);
  isBicolor = R.random_bool(0.2);
  if (isBicolor) colors = [R.random_choice(colors), R.random_choice(colors)];

  recursivity = R.random_int(7, 13);
  magnifier = R.random_int(5, 150);

  for (let i = 0; i < 100000; i++) {
    randDim.push(R.random_num(0.2, 0.8));
  }

  for (let j = 0; j < 100000; j++) {
    randColor.push(R.random_choice(colors));
  }

  let features = {
    "Color Palette": colors != palettes[5] ? "Colors" : "Grayscale",
    Recursivity: recursivity,
    Type: isBicolor ? "Bi-Color" : "Standard",
    Frequency: magnifier
  };

  return features;
};

function setup() {

  // initialise Random class
  R = new Random();
  calculateFeatures();
  // Grab the smaller of the window sizes and use that as the canvas size.
  smallerDimension = windowWidth < windowHeight ? windowWidth : windowHeight;
  c = createCanvas(smallerDimension, smallerDimension);

  rectMode(CENTER);
}

function draw() {
  background(0);
  randDimCounter = 0;
  randColorCounter = 0;
  let rect = new MagicRect(smallerDimension / 2, smallerDimension / 2, smallerDimension, smallerDimension, recursivity);
  rect.drawRect();
}

class MagicRect {
  constructor(xPos, yPos, width, height, depth) {
    this.xPos = xPos;
    this.yPos = yPos;
    this.width = width;
    this.height = height;
    this.depth = depth;
  }

  drawRect() {
    let scaleX = abs(cos((millis() + this.xPos * magnifier) / 1000));
    let scaleY = abs(cos((millis() + this.yPos * magnifier) / 1000));

    if (!enableAnimation) {
      scaleX = 1;
      scaleY = 1;
    }
    const randomDim = getRandomDim();
    const randomColor = getRandomColor();

    if (this.depth >= 0) {
      let rectWidth = randomDim * this.width;
      let rectHeight = randomDim * this.height;

      if (enableInteraction) {
        if (this.depth === recursivity) {
          rectWidth = constrain(mouseX, 0, this.width);
        } else if (this.depth === recursivity - 1) {
          rectHeight = constrain(mouseY, 0, this.height);
        }
      }

      const halfWidth = this.width / 2;
      const halfHeight = this.height / 2;
      const halfRectWidth = rectWidth / 2;
      const halfRectHeight = rectHeight / 2;

      if (this.width < this.height) {
        const upperMagicRect = new MagicRect(this.xPos, this.yPos - halfHeight + halfRectHeight, this.width, rectHeight, this.depth - 1);
        upperMagicRect.drawRect();

        const lowerMagicRect = new MagicRect(this.xPos, this.yPos + halfHeight - (this.height - rectHeight) / 2, this.width, this.height - rectHeight, this.depth - 1);
        lowerMagicRect.drawRect();
      } else {
        const leftMagicRect = new MagicRect(this.xPos - halfWidth + halfRectWidth, this.yPos, rectWidth, this.height, this.depth - 1);
        leftMagicRect.drawRect();

        const rightMagicRect = new MagicRect(this.xPos + halfWidth - (this.width - rectWidth) / 2, this.yPos, this.width - rectWidth, this.height, this.depth - 1);
        rightMagicRect.drawRect();
      }
    } else {
      push();

      noStroke();
      fill(randomColor);
      rect(this.xPos, this.yPos, (this.width - 2) * scaleX, (this.height - 2) * scaleY, 0, this.randCorner(this.width, this.height), 0, this.randCorner(this.width, this.height));
      pop();
    }
  }

  randCorner(width, height) {
    return getRandomDim() < 0.5 ? max(width, height) : 0;
  }
}


function getRandomDim() {
  let dim = randDim[randDimCounter];
  randDimCounter++;
  return dim;
}

function getRandomColor() {
  let col = randColor[randColorCounter];
  randColorCounter++;
  return col;
}

