		colors: ["#6c81c2", "#f3b8d2", "#b59dba", "#ffcd38", "#ffffff", "#e76046"]
	},
	{
		name: "Elegant Ensemble",
		colors: ["#e3923d", "#915062", "#5c372b", "#e36448", "#d7c787", "#dd83a5", "#324378", "#ffffff"]
	},
	{
		name: "Harmonious Hue",
		colors: ["#debb2f", "#ffcd38", "#474883", "#000000", "#ffffff", "#e5bfd4"]
	},
	{
		name: "Synchronized Spectrum",
		colors: ["#9689b5", "#ffb396", "#000000", "#ffffff", "#ff8d29", "#ff87ff", "#3b8584", "#f3f3e7"]
	}
	

];

let canvas;

// Composition Variables

let chosenPalette = palettes[dcP[0] % palettes.length];
let colores = shuffleArray(chosenPalette.colors);

let chosenPaletteName = chosenPalette.name;

let chosenBkgrColor = bgPalette[dcP[1] % bgPalette.length];

const backgroundColorStrategies = [
	'Plain',
	'Radial Gradient',
	'Lined Gradient'];
let chosenBackgroundColorStrategy = backgroundColorStrategies[dcP[2] % backgroundColorStrategies.length];

const colorSchemes = [
    "Zebra Stripes",
    "Checkerboard",
    "Random Bias",
    "Gradient Feel",
    "Gradient",
    "Diagonal Gradient",
    "Radial Gradient",
    "Mirrored Radial Gradient"
];

let chosenColorScheme = colorSchemes[dcP[3] % colorSchemes.length];

const densityStrategies = [
	"Low",
	"Medium",
	"High",
	"Super High"
]; 
let chosenDensityStrategy = densityStrategies[dcP[4] % densityStrategies.length];

function getCanvasDimensions() {
	// Calculate the window's current aspect ratio
	let menor=innerWidth<innerHeight?innerWidth:innerHeight;
	// Return the calculated canvas dimensions
	return {
		width: innerWidth,
		height: innerHeight,
		menor: menor
	};
}

const solidCircles = [];
const outlineCircles = [];
const lines = [];
let lineColor = null;

let dimensions = getCanvasDimensions();
let shorterSide = dimensions.menor;

let strokeThickness;
let marginPx = shorterSide * 0.05;
let margin = marginPx / shorterSide;

const backgroundConfig = {};

function scaledValue( val, rounded = false ) {
	let scaled = val * shorterSide;
	return rounded ? Math.round( scaled ) : scaled;
}

function setup() {
	noiseSeed(seed + 2);
	noiseDetail(10, .1);

	canvas = createCanvas(innerWidth, innerHeight);
	noLoop();

	buildBackground();

	if (R.r_dec() < 0.1) {
		chosenPalette.randomBias = "#000000";
	}
	else if (R.r_dec() < 0.2) {
		chosenPalette.randomBias = "#ffffff";
	}
	else {
		chosenPalette.randomBias = R.r_choice(colores);
	}

	buildElements();
	
	drawElements();
}


function buildBackground() {
	switch(chosenBackgroundColorStrategy) {
		case 'Plain':
			// nothing
			break;
		case 'Lined Gradient':
			backgroundConfig.col1 = R.r_choice(colores);
			do {
				backgroundConfig.col2 = R.r_choice(colores);
			} while (backgroundConfig.col1 === backgroundConfig.col2);

			backgroundConfig.numColors = R.r_int(32, 64);
			break;
		case 'Radial Gradient':
			backgroundConfig.col1 = R.r_choice(colores);
			do {
				backgroundConfig.col2 = R.r_choice(colores);
			} while (backgroundConfig.col1 === backgroundConfig.col2);
			backgroundConfig.numColors = R.r_int(25, 52);
			break;
	}
}

function drawElements() {

	rectMode(CENTER);
	background(chosenBkgrColor);
	applyBackground(chosenBackgroundColorStrategy);

	noStroke();
	translate(-shorterSide/2,-shorterSide/2);
	solidCircles.forEach(data => {
		fill( selectFillColor( scaledValue(data.x), scaledValue(data.y), scaledValue(data.size) ) );
		drawSection(data.decorations);
	});

	noFill();
	strokeCap(PROJECT);
	strokeWeight( scaledValue(strokeThickness) );

	stroke(lineColor);

	lines.forEach(data => {
		if ( data.hasOwnProperty('rotation') ) {
			push();
			translate( scaledValue(data.translateX), scaledValue(data.translateY) );
			rotate( data.rotation );
			drawSection(data.decorations);
			pop();
		}
		else {
			drawSection(data.decorations);
		}
	});

	outlineCircles.forEach(data => {
		drawSection(data.decorations);
	});

}

function buildElements() {

	rectMode(CENTER);

	let n = R.r_int(4, 22);

	let sPx = (shorterSide-2*marginPx)/n;
	let s = sPx / shorterSide;

	let offset1 = 0;
	let offset2 = s/2;

	if (R.r_dec() < 0.3) {
		[offset1, offset2] = [offset2, offset1];
	}

	let gridSize = ( offset1 !== 0 ) ? n - 1 : n;
	let startX = margin + offset1;
	let startY = margin + offset1;
	let row = 0
	let column = 0;
	let x = startX;
	let y = startY;

	//Solid circles
	for ( let cell = 1; cell <= (gridSize * gridSize); cell++ ) {
		solidCircles.push({ x, y, size:s, decorations:buildSection(x, y, s) });
		x += s;
		column++;
		if ( column >= gridSize ) {
			column = 0;
			x = startX;
			y += s;
			row++;
		}
	}

	do {
		lineColor = R.r_choice(colores);
	} while (lineColor === chosenBkgrColor);

	let chance = R.r_dec();

	gridSize = ( offset2 !== 0 ) ? n - 1 : n;
	startX = margin + offset2;
	startY = margin + offset2;
	row = 0
	column = 0;
	x = startX;
	y = startY;

	if (chance < 0.1) {
		for ( let cell = 1; cell <= (gridSize * gridSize); cell++ ) {
			lines.push({
				x:-s/2,
				y:-s/2,
				size:s,
				rotation: R.r_choice([0, PI/4, PI/2, 3*PI/4]),
				translateX:x + s/2,
				translateY:y + s/2,
				decorations:buildSection(-s/2, -s/2, s)
			});
			x += s;
			column++;
			if ( column >= gridSize ) {
				column = 0;
				x = startX;
				y += s;
				row++;
			}
		}
	} else if(chance > 0.9){
		for ( let cell = 1; cell <= (gridSize * gridSize); cell++ ) {
			if ( R.r_dec() < 0.2 ) {
				lines.push({
					x:-s/2,
					y:-s/2,
					size:s,
					rotation: R.r_choice([0, PI/4, PI/2, 3*PI/4]),
					translateX:x + s/2,
					translateY:y + s/2,
					decorations:buildSection(-s/2, -s/2, s)
				});
			}
			else {
				lines.push({ x, y, size:s, decorations:buildSection(x, y, s) });
			}
			x += s;
			column++;
			if ( column >= gridSize ) {
				column = 0;
				x = startX;
				y += s;
				row++;
			}
		}
	} else {
		// outline circles
		for ( let cell = 1; cell <= (gridSize * gridSize); cell++ ) {
			outlineCircles.push({ x, y, size:s, decorations:buildSection(x, y, s) });
			x += s;
			column++;
			if ( column >= gridSize ) {
				column = 0;
				x = startX;
				y += s;
				row++;
			}
		}
	}
}

function buildSection(x, y, s) {
    const topLeft = {x, y};
    const topRight = {x: x + s, y};
    const bottomRight = {x: x + s, y: y + s};
    const bottomLeft = {x, y: y + s};
    const center = {x: x + s / 2, y: y + s / 2};

    const decorations = [];

	let lineType = R.r_choice(['straight', 'dashed', 'squared']);

	strokeThickness = s / R.r_int(16, 20);    
	
	const probabilityHorizontal = R.r_choice([0.2, 0.3, 0.5, 0.8]);
	const probabilityVertical = R.r_choice([0.2, 0.3, 0.5, 0.8]);

	let startH, endH, startV, endV;

	if (R.r_dec() < probabilityHorizontal) {
		startH = topLeft;
		endH = topRight;
	} else {
		startH = bottomLeft;
		endH = bottomRight;
	}

	if (R.r_dec() < probabilityVertical) {
		startV = topLeft;
		endV = bottomLeft;
	} else {
		startV = topRight;
		endV = bottomRight;
	}

	let ellipseSizeFactor;
	let ellipseOffset = 0; 

	switch (chosenDensityStrategy) {
		case "Low": 
			ellipseSizeFactor = 0.6;
			if (R.r_dec() < 0.2) addEllipse(center, s * ellipseSizeFactor, s * ellipseSizeFactor);
			break;
		case "Medium":
			ellipseSizeFactor = 0.5;
			if (R.r_dec() < 0.5) addEllipse({ x: center.x + ellipseOffset, y: center.y }, s * ellipseSizeFactor, s * ellipseSizeFactor);
			break;
		case "High": 
			ellipseSizeFactor = 0.4;
			if (R.r_dec() < 0.8) addEllipse(center, s * ellipseSizeFactor, s * ellipseSizeFactor);
			break;
		case "Super High":
			ellipseSizeFactor = 0.5;
			addEllipse(center, s * ellipseSizeFactor, s * ellipseSizeFactor);
			break;
	}

	switch (lineType) {
		case 'straight':
			addLine(startH, endH);
			if (R.r_dec() < 0.8) {
				addLine(startV, endV);
				if (chosenDensityStrategy === "Medium") {
					ellipseOffset = s * 0.5; 
				}
			}
			break;

		case 'dashed':
			const segments = R.r_int(6, 20);
			addDashedLine(startH, endH, segments);
			if (R.r_dec() < probabilityVertical) {
				addDashedLine(startV, endV, segments);
			}   
			break;

		case 'squared':
			addSquareTxLine(startH, endH, 10);
			if (R.r_dec() < 0.5) {
				addSquareTxLine(startV, endV, 10);
			}
			if (chosenDensityStrategy === "High" || chosenDensityStrategy === "Super High") {
				ellipseOffset = s * 0.2; 
			}
			break;
	}

	if (R.r_dec() < 0.3) { 
		if (R.r_dec() < 0.7) {  
			addEllipse(center, s / 4, s / 4);
		}
		if (R.r_dec() < 0.2) { 
			addEllipse(center, s / 16, s / 16);
		}
	} 

	function addLine(start, end) {
		decorations.push({type: "line", x1: start.x, y1: start.y, x2: end.x, y2: end.y});
	}

    function addEllipse(position, w, h) {
        decorations.push({type: "ellipse", ...position, w, h, strokeThickness});
    }

	function addDashedLine(start, end, segments) {
		let stepX = (end.x - start.x) / segments;
		let stepY = (end.y - start.y) / segments;
	
		for (let i = 0; i < segments; i += 2) {
			let x1 = start.x + i * stepX;
			let y1 = start.y + i * stepY;
			let x2 = start.x + (i + 1) * stepX;
			let y2 = start.y + (i + 1) * stepY;
			addLine({x: x1, y: y1}, {x: x2, y: y2});
		}
	}
	
	function addSquareTxLine(start, end, segments) {
		let distance = dist(start.x, start.y, end.x, end.y);
		let step = distance / segments;
		let fraction = 0.1;  
	
		let sqCount = 0;  
		let skipPattern = R.r_choice([2 ,3, 6]);  
		let patternIndex = 0;  
		
		for (let i = 0; i < segments; i++) {
			let x = lerp(start.x, end.x, i / segments);
			let y = lerp(start.y, end.y, i / segments);
			let nextX = lerp(start.x, end.x, (i + fraction) / segments);
			let nextY = lerp(start.y, end.y, (i + fraction) / segments);			
			
			if (sqCount != skipPattern[patternIndex]) {				
				addLine({x: x, y: y}, {x: nextX, y: nextY});
				sqCount++;
			} else {
				sqCount = 0;
				patternIndex = (patternIndex + 1) % skipPattern.length;
			}
	
			i += R.r_choice([2 ,3, 5]);
		}
	}

    return decorations;
}

function drawSection( decorations ) {

	decorations.forEach(data => {
		switch( data.type ) {
			case "line" :
				line(scaledValue(data.x1), scaledValue(data.y1), scaledValue(data.x2), scaledValue(data.y2));
				break;
			case "ellipse" :
				ellipse(scaledValue(data.x), scaledValue(data.y), scaledValue(data.w), scaledValue(data.h));
				break;
		}
	});
}

function addLine(start, end, decorations) {
    decorations.push({type: "line", x1: start.x, y1: start.y, x2: end.x, y2: end.y});
}

function addEllipse(position, w, h, decorations) {
    decorations.push({type: "ellipse", ...position, w, h});
}

function addArc(position, w, h, start, stop, decorations) {
    decorations.push({type: "arc", ...position, w, h, start, stop});
}

function addDashedLine(start, end, segments, decorations) {
    let stepX = (end.x - start.x) / segments;
    let stepY = (end.y - start.y) / segments;

    for (let i = 0; i < segments; i += 2) {
        let x1 = start.x + i * stepX;
        let y1 = start.y + i * stepY;
        let x2 = start.x + (i + 1) * stepX;
        let y2 = start.y + (i + 1) * stepY;
        addLine({x: x1, y: y1}, {x: x2, y: y2}, decorations);
    }
}

function addSquareTxLine(start, end, segments, decorations) {
    let distance = dist(start.x, start.y, end.x, end.y);
		let step = distance / segments;
		let fraction = 0.1;  
	
		let sqCount = 0;  
		let skipPattern = R.r_choice([2 ,3, 6]);  
		let patternIndex = 0; 
	
		for (let i = 0; i < segments; i++) {
			let x = lerp(start.x, end.x, i / segments);
			let y = lerp(start.y, end.y, i / segments);
			let nextX = lerp(start.x, end.x, (i + fraction) / segments);
			let nextY = lerp(start.y, end.y, (i + fraction) / segments);			
			
			if (sqCount != skipPattern[patternIndex]) {
				
				addLine({x: x, y: y}, {x: nextX, y: nextY});
				sqCount++;
			} else {
				
				sqCount = 0;
				patternIndex = (patternIndex + 1) % skipPattern.length;
			}
	
			i += 3;  
		}
}

function applyBackground(strategy) {

	switch(strategy) {
		case 'Plain':
			setPlainBackground();
			break;
		case 'Lined Gradient':
			drawLinedGradientBackground();
			break;
		case 'Radial Gradient':
			drawRadialGradient();
			break;
	}
}

function selectFillColor(x, y, s) {
    const colors = ["#000000", "#ffffff"].concat(colores);
    const paletteLength = colores.length;
    let amount, gradientPos, colorStartIndex, colorEndIndex, innerColor, outerColor,distanceToCenter;
    const centerX = shorterSide / 2;
    const centerY = shorterSide / 2;

    switch (chosenColorScheme) {
		case "Zebra Stripes":
			return Math.floor(y / s) % 2 == 0 ? "#000000" : "#ffffff";
	
		case "Checkerboard":
			return (Math.floor(x / s) + Math.floor(y / s)) % 2 == 0 ? "#000000" : "#ffffff";
	
		case "Random Bias":
			return chosenPalette.randomBias;
	
		case "Gradient Feel":
			gradientPos = y / shorterSide;
			colorStartIndex = Math.floor((paletteLength - 1) * gradientPos);
			colorEndIndex = Math.min(colorStartIndex + 1, paletteLength - 1);
			amount = map(y % s, 0, s, 0, 1);
			break;
	
		case "Gradient":
			amount = map(y, margin, shorterSide - margin, 0, 1);
			colorStartIndex = 0;
			colorEndIndex = paletteLength - 1;
			break;
	
		case "Diagonal Gradient":
			let startCorner = createVector(margin, margin);
			let endCorner = createVector(shorterSide - margin, shorterSide - margin);
			amount = map(startCorner.dist(createVector(x, y)), 0, startCorner.dist(endCorner), 0, 1);
			colorStartIndex = 0;
			colorEndIndex = paletteLength - 1;
			break;
	
		case "Radial Gradient":
			const maxDistance = dist(centerX, centerY, width, height);
			distanceToCenter = dist(x, y, centerX, centerY);
			gradientPos = map(distanceToCenter, 0, maxDistance, 0, 1);
			colorStartIndex = Math.floor((paletteLength - 1) * gradientPos);
			colorEndIndex = Math.min(colorStartIndex + 1, paletteLength - 1);
			amount = gradientPos * (paletteLength - 1) - colorStartIndex;
			break;
	
		case "Mirrored Radial Gradient":
			gradientPos = map(Math.abs(y - centerY), 0, shorterSide / 2, 0, 1);
			colorStartIndex = Math.floor((paletteLength - 1) * gradientPos);
			colorEndIndex = Math.min(colorStartIndex + 1, paletteLength - 1);
			amount = gradientPos * (paletteLength - 1) - colorStartIndex;
			break;
	}
	
    innerColor = color(colores[colorStartIndex]);
    outerColor = color(colores[colorEndIndex]);
    return lerpColor(innerColor, outerColor, amount);
}

function setPlainBackground() {
	translate(width/2,height/2);
}

function drawLinedGradientBackground() {
	let gradientColors = generateGradientColors(backgroundConfig.col1, backgroundConfig.col2, backgroundConfig.numColors);
	let gradient = createGraphics(width, height);
	let steps = 200;
	let stripHeight = height/steps;
	gradient.rectMode(CENTER);
	
	for (let i = 0; i <= steps; i++) {
		let colorIndex = Math.floor(map(i, 0, steps, 0, gradientColors.length - 1));
		let c = gradientColors[colorIndex];
		gradient.fill(c);
		gradient.noStroke();
		gradient.rect(width/2, i*stripHeight+stripHeight/2, width, stripHeight*1.2);
	}
	translate(width/2,height/2);
	imageMode(CENTER);
	image(gradient, 0, 0);
}

function drawRadialGradient() {
	let gradientColors = generateGradientColors(backgroundConfig.col1, backgroundConfig.col2, backgroundConfig.numColors);
	let gradient = createGraphics(width, height);
	gradient.noStroke();
	gradient.translate(width/2,height/2);
	gradient.background(backgroundConfig.col2);
	let halfWidth = shorterSide / 2;
	let halfHeight = shorterSide / 2;
	let maxRadius = Math.sqrt(halfWidth * halfWidth + halfHeight * halfHeight);
	let decrement = maxRadius / backgroundConfig.numColors;
	for (let r = maxRadius; r > 0; r -= decrement) {
		let c = gradientColors.pop();
		if (c) {
			gradient.fill(c);
			gradient.ellipse(0, 0, r * 2, r * 2);
		}
	}
	translate(width/2,height/2);
	imageMode(CENTER);
	image(gradient, 0, 0);
}



function generateGradientColors(startColor, endColor, numColors) {
	let colors = [];
	for (let i = 0; i < numColors; i++) {
		let inter = map(i, 0, numColors - 1, 0, 1);
		let c = lerpColor(color(startColor), color(endColor), inter);
		colors.push(c);
	}
	return colors;
}

function draw(){
}

function debounce(func, wait, immediate) {
	let timeout;
	return function() {
		let context = this, args = arguments;
		let later = function() {
			timeout = null;
			if (!immediate) func.apply(context, args);
		};
		let callNow = immediate && !timeout;
		clearTimeout(timeout);
		timeout = setTimeout(later, wait);
		if (callNow) func.apply(context, args);
	};
}

function onResize() {
	dimensions = getCanvasDimensions();
	shorterSide = dimensions.menor;
	
	marginPx = shorterSide * 0.05; 
	margin = marginPx / shorterSide;

	resizeCanvas(innerWidth, innerHeight);

	drawElements();
}

window.addEventListener('resize', debounce(onResize, 100));


keyPressed=()=>{
	if (key === 's') {
		save(tokenData.hash);
	}
}


function shuffleArray(array) {
	let clonedArray = [...array];
    for (let i = clonedArray.length - 1; i > 0; i--) {
        let j = Math.floor(R.r_dec() * (i + 1));
        [clonedArray[i], clonedArray[j]] = [clonedArray[j], clonedArray[i]];
    }
    return clonedArray;
}

class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}

let R = new Random();

function setup() {
  createCanvas(windowWidth, windowHeight);
  noLoop();
  colorMode(RGB, 255, 255, 255, 1);
  noStroke();

  let seed = R.random_int(0, 99999);
  randomSeed(seed);
  noiseSeed(seed);

  let bgColor = R.random_bool(0.5) ? color('#FAF7FB') : color('#222222');
  background(bgColor);
}

function draw() {
  for (let i = 0; i < 70; i++) {
    let scale = width / 3;
    parallelogram(R.random_num(-scale, width + scale), R.random_num(-scale, height + scale));
    gradientCircle(R.random_num(0, width), R.random_num(0, height), 20 + 400 * pow(R.random_dec(), 4));
  }
}

function gradientCircle(_x, _y, _size) {
  let g = createGraphics(width, height);
  g.push();
  g.noStroke();
  g.blendMode(MULTIPLY);
  g.translate(_x, _y);

  let gradfunc;
  for (let i = 0; i < 2; i++) {
    gradfunc = R.random_choice([cyanGradient, magentaGradient, yellowGradient]);
    g.rotate(R.random_num(0, TAU));
    g.drawingContext.fillStyle = gradfunc(_size);
    g.circle(R.random_num(-6, 6), R.random_num(-6, 6), _size);
  }

  g.pop();
  image(g, 0, 0);
}

function cyanGradient(_size) {
  let gradient = drawingContext.createLinearGradient(_size / 2, 0, -_size / 2, 0);
  gradient.addColorStop(0, color(97, 244, 255, 0.0));
  gradient.addColorStop(1, color(97, 244, 255, 0.7));
  return gradient;
}

