function parallelogram(_x, _y) {
  let pos = createVector(_x, _y);
  const ang = -PI / 9;
  const shapeHeight = 2 + 150 * pow(R.random_dec(), 3);
  const shapeLength = R.random_num(100, 350);
  push();
  noStroke();
  let rightVec = p5.Vector.fromAngle(ang, shapeLength);
  let leftVec = p5.Vector.fromAngle(ang + PI, shapeLength);
  let upVec = createVector(0, -shapeHeight / 2);
  let downVec = createVector(0, shapeHeight / 2);
  let vertices = [];
  vertices.push(p5.Vector.add(pos, p5.Vector.add(rightVec, upVec)));
  vertices.push(p5.Vector.add(pos, p5.Vector.add(rightVec, downVec)));
  vertices.push(p5.Vector.add(pos, p5.Vector.add(leftVec, downVec)));
  vertices.push(p5.Vector.add(pos, p5.Vector.add(leftVec, upVec)));
  let gradient = drawingContext.createLinearGradient(vertices[0].x, vertices[0].y, vertices[2].x, vertices[2].y);
  gradient.addColorStop(0, color(250, 230, 70, 0.1));
  gradient.addColorStop(1, color(40, 40, 50, 0.2));
  drawingContext.fillStyle = gradient;
  beginShape();
  for (let v of vertices) {
    vertex(v.x, v.y);
  }
  endShape(CLOSE);
  pop();
}

class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}

let R = new Random();
let h = [];
const noiseVal = 0.003;
const COUNT = 5;
let colorSeed;
let brightSeed;
let skyOff;
let sunX;
let sunY;
let sunSize;

function setup() {
  R = new Random();
  createCanvas(windowWidth, windowHeight);
  colorMode(HSB, 100);
  rectMode(CENTER);
  angleMode(DEGREES);
  
  let seed = R.random_int(0,99999);
  randomSeed(seed);
  noiseSeed(seed);

  loadVars();
  loadHills();
}

const loadVars = () => {
  colorSeed = R.random_num(0, 100);
  brightSeed = R.random_num(30, 100);
  skyOff = R.random_num(0, 50);
  sunX = R.random_num(0, width);
  sunY = R.random_num(-50, 20);
  sunSize = R.random_num(80, 200);
};

function draw() {
  displaySky();
  displayGlare();
  displayHills();
  displayGlare(true);
  displayGrain();
  noLoop();
}

const displaySky = () => {
  for (let i = 0; i < height / 2; i++) {
    stroke((colorSeed + skyOff)%100, 100, (i + 60) / 5);

    line(0, i, width, i);
  }
  stroke(colorSeed, 20, 100);
  fill((colorSeed + skyOff)%100, 25, 100);
  ellipse(sunX, height / 3 + sunY, sunSize);
};

const displayGlare = (light) => {
  for (let i = 0; i < width * 2; i += 10) {
    noFill();
    strokeWeight(1 - i * 0.0001);
    if (light) {
      stroke(colorSeed, 20, 100, 10);
    } else {
      stroke(colorSeed, 20, 100, 100 - i / 10);
    }

    ellipse(sunX, height / 3 + sunY, i);
  }
};

const displayHills = () => {
  for (let hill of h) {
    hill.display();
    hill.move();
  }
};

const loadHills = () => {
  for (let y = height / 2, o = 0; y < height + 300; y += COUNT, o += 2) {
    h.push(new Hill(y + o, 0));
  }
};
class Hill {
  constructor(y, t) {
    this.y = y;
    this.t = t;
  }

  display() {
    let sat = map(this.y - 500, 0, height, 20, 100);
    strokeWeight(1);
    stroke(colorSeed, sat, brightSeed < 50 ? 100 : 40);
    fill(colorSeed, sat, brightSeed);
    beginShape();
    vertex(-100, height + 100);
    for (let i = 0; i < width; i += 1) {
      let ny = noise(i * noiseVal, this.y / 100, this.t * noiseVal);
      let yOff = map(ny, 0, 1, 0, height / 3);
      vertex(i, this.y - yOff);
    }
    vertex(width + 100, height + 100);

    endShape(CLOSE);
    
    //trees
    for (let i = 0; i < width; i += 1) {
      
      if(random(100)<0.5){
        fill(colorSeed, sat, brightSeed-20);
        noStroke();
        let ny = noise(i * noiseVal, this.y / 100, this.t * noiseVal);

        let yOff = map(ny, 0, 1, 0, height / 3);

        let tMap = map(this.y,0,height,0,3)
        translate(i, this.y - yOff)
        rotate(random(-10,10))
        let rH = random(3,10)
        rect(0,0,tMap,rH+this.y/100)
        ellipse(0,-(rH+this.y/100)/2,tMap*2)
        resetMatrix();
      }
    }
  }

  move() {
    this.t += 10;
  }
}
function displayGrain() {
  for(let i = 0; i < 300000; i++){
    noStroke();
    fill(colorSeed, 20, 100, 5);
    rect(R.random_num(0, width), R.random_num(0, height), R.random_num(0, 2), R.random_num(0, 2));
  }
}

