function chrGradient() {
  chr = [color(255, 255, 0),color(255, 127, 0),color(255, 0, 0),color(148, 0, 211),color(75, 0, 130),color(0, 0, 255),color(0, 255, 0)];
  let rectX = 0;
  let rectY = 0;
  let rectWidth = iw;
  let rectHeight = ih;
  let numColors = chr.length;

  // Draw the vertical gradient
  cg.push();
  for (let y = 0; y < rectWidth; y++) {
    let t = map(y, 0, rectWidth, 0, 1);
    let index = floor(t * (numColors - 1));
    let inter = lerpColor(chr[index], chr[index + 1], t * (numColors - 1) - index);
    cg.stroke(inter);
    // cg.line(rectX, rectY + y, rectX + rectWidth, rectY + y);
    cg.line(rectX + y, rectY, rectX + y, rectY + rectHeight);
  }
  cg.pop();
}

